import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import ChatBubble from './ChatBubble';

const Chat = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [imageFile, setImageFile] = useState(null);
  const [imagePreview, setImagePreview] = useState(null);
  const messagesEndRef = useRef(null);
  const textareaRef = useRef(null);

  useEffect(() => {
    fetchChatHistory();
  }, []);

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const fetchChatHistory = async () => {
    try {
      const token = localStorage.getItem('token');
      
      if (!token) {
        console.error('Token bulunamadÄ±');
        return;
      }
      
      const response = await axios.get('http://localhost:5000/api/chat-history', {
        headers: { Authorization: `Bearer ${token}` }
      });
      
      if (response.data.chatHistory) {
        setMessages(response.data.chatHistory);
      }
    } catch (error) {
      console.error('Sohbet geÃ§miÅŸi yÃ¼klenirken hata:', error);
    }
  };

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSubmit(e);
    }
  };

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      setImageFile(file);
      const reader = new FileReader();
      reader.onloadend = () => {
        setImagePreview(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const clearImage = () => {
    setImageFile(null);
    setImagePreview(null);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const message = input.trim();
    
    if (!message && !imageFile) return;
    
    try {
      setIsLoading(true);
      const token = localStorage.getItem('token');
      
      if (!token) {
        console.error('Token bulunamadÄ±');
        return;
      }
      
      // KullanÄ±cÄ± mesajÄ±nÄ± ekleyin
      const userMessage = {
        role: 'user',
        content: message
      };
      
      setMessages((prev) => [...prev, userMessage]);
      setInput('');
      
      // EÄŸer gÃ¶rsel varsa, farklÄ± bir endpoint kullanarak gÃ¶rsel analizi yapÄ±n
      if (imageFile) {
        const formData = new FormData();
        formData.append('image', imageFile);
        
        await axios.post(
          'http://localhost:5000/api/analyze-image',
          formData,
          {
            headers: {
              Authorization: `Bearer ${token}`,
              'Content-Type': 'multipart/form-data'
            }
          }
        );
        
        clearImage();
      } else {
        // Normal metin mesajÄ± gÃ¶nderme
        const response = await axios.post('http://localhost:5000/api/chat',
          { message },
          { headers: { Authorization: `Bearer ${token}` } }
        );
        
        // Asistan yanÄ±tÄ±nÄ± ekleyin (eÄŸer yanÄ±t mevcutsa)
        if (response.data.response) {
          const assistantMessage = {
            role: 'assistant',
            content: response.data.response
          };
          
          setMessages((prev) => [...prev, assistantMessage]);
        }
      }
    } catch (error) {
      console.error('Mesaj gÃ¶nderilirken hata:', error);
      
      // Hata mesajÄ±nÄ± sohbete ekleyin
      const errorMessage = {
        role: 'assistant',
        content: 'ÃœzgÃ¼nÃ¼m, bir hata oluÅŸtu. LÃ¼tfen daha sonra tekrar deneyin.'
      };
      
      setMessages((prev) => [...prev, errorMessage]);
    } finally {
      setIsLoading(false);
      setTimeout(scrollToBottom, 100);
    }
  };

  return (
    <div className="chat-container">
      <div className="bg-white rounded-lg shadow-md overflow-hidden flex flex-col h-[70vh]">
        <div className="p-4 bg-diet-green text-white">
          <h2 className="text-xl font-semibold">Diyet Dostu ðŸ¥—</h2>
          <p className="text-sm">SaÄŸlÄ±klÄ± yaÅŸam asistanÄ±nÄ±z</p>
        </div>
        
        <div className="flex-grow overflow-y-auto p-4">
          {messages.length === 0 ? (
            <div className="flex items-center justify-center h-full">
              <div className="text-center">
                <div className="text-4xl mb-4">ðŸ‘‹</div>
                <p className="text-xl font-medium text-gray-700 mb-2">Diyet Dostu'na HoÅŸ Geldin!</p>
                <p className="text-gray-500 mb-4">Beslenme, diyet veya fitness hakkÄ±nda bir ÅŸeyler sor.</p>
              </div>
            </div>
          ) : (
            messages.map((msg, index) => (
              <ChatBubble 
                key={index}
                message={msg}
              />
            ))
          )}
          
          {isLoading && (
            <div className="flex items-center space-x-2 text-gray-500 p-3 bg-gray-100 rounded-lg max-w-[80%] mr-auto">
              <div className="flex space-x-1">
                <div className="w-2 h-2 rounded-full bg-diet-green animate-bounce" style={{ animationDelay: '0ms' }}></div>
                <div className="w-2 h-2 rounded-full bg-diet-green animate-bounce" style={{ animationDelay: '300ms' }}></div>
                <div className="w-2 h-2 rounded-full bg-diet-green animate-bounce" style={{ animationDelay: '600ms' }}></div>
              </div>
              <p>Diyet Dostu yanÄ±t yazÄ±yor...</p>
            </div>
          )}
          
          <div ref={messagesEndRef} />
        </div>

        <div className="p-4 border-t">
          <form onSubmit={handleSubmit} className="flex items-end gap-2">
            <div className="flex-1 relative">
              <textarea
                value={input}
                onChange={(e) => setInput(e.target.value)}
                placeholder="Bir mesaj yaz..."
                className="w-full p-3 border rounded-lg resize-none focus:outline-none focus:ring-2 focus:ring-diet-green"
                rows={1}
                ref={textareaRef}
                onKeyDown={handleKeyDown}
              />
              {imagePreview && (
                <div className="relative inline-block mt-2">
                  <img
                    src={imagePreview}
                    alt="Preview"
                    className="h-20 rounded border"
                  />
                  <button
                    type="button"
                    className="absolute -top-2 -right-2 bg-red-500 text-white rounded-full w-6 h-6 flex items-center justify-center"
                    onClick={clearImage}
                  >
                    Ã—
                  </button>
                </div>
              )}
            </div>
            
            <div className="flex items-center gap-2">
              <label className="cursor-pointer text-diet-green hover:text-diet-green-dark">
                <input
                  type="file"
                  accept="image/*"
                  className="hidden"
                  onChange={handleImageUpload}
                />
                <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 16l4.586-4.586a2 2 0 012.828 0L16 16m-2-2l1.586-1.586a2 2 0 012.828 0L20 14m-6-6h.01M6 20h12a2 2 0 002-2V6a2 2 0 00-2-2H6a2 2 0 00-2 2v12a2 2 0 002 2z" />
                </svg>
              </label>
              
              <button
                type="submit"
                disabled={!input.trim() && !imageFile}
                className={`p-3 rounded-full ${
                  !input.trim() && !imageFile
                    ? 'bg-gray-300 text-gray-500 cursor-not-allowed'
                    : 'bg-diet-green text-white hover:bg-diet-green-dark'
                }`}
              >
                <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 19l9 2-9-18-9 18 9-2zm0 0v-8" />
                </svg>
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Chat;
