import React from 'react';
import { Link } from 'react-router-dom';

const Header = ({ isAuthenticated, onLogout }) => {
  const headerStyle = {
    position: 'sticky',
    top: 0,
    zIndex: 99999,
    background: 'linear-gradient(45deg, #4CAF50, #8BC34A)',
    boxShadow: '0 4px 10px rgba(0,0,0,0.12)',
  };

  const navLinkStyle = {
    position: 'relative',
    zIndex: 99999,
    color: 'white',
    fontWeight: 500,
    padding: '0.5rem 1rem',
    transition: 'all 0.3s ease'
  };

  const navButtonStyle = {
    position: 'relative',
    zIndex: 99999,
    background: '#FF9800',
    color: 'white',
    padding: '0.5rem 1rem',
    borderRadius: '8px',
    fontWeight: 600,
    marginLeft: '1rem',
    transition: 'all 0.3s ease',
    border: 'none',
    cursor: 'pointer'
  };

  return (
    <header className="header" style={headerStyle}>
      <div className="container mx-auto flex justify-between items-center">
        <Link to="/" className="logo" style={{ position: 'relative', zIndex: 99999, color: 'white', fontWeight: 700, fontSize: '1.5rem', textDecoration: 'none' }}>
          Diyet Dostu <span className="logo-icon">ðŸ¥—</span>
        </Link>
        
        {isAuthenticated ? (
          <nav className="flex items-center" style={{ position: 'relative', zIndex: 99999 }}>
            <Link to="/chat" style={navLinkStyle}>
              Sohbet
            </Link>
            <Link to="/profile" style={navLinkStyle}>
              Profil
            </Link>
            <button 
              onClick={onLogout}
              style={navButtonStyle}
            >
              Ã‡Ä±kÄ±ÅŸ
            </button>
          </nav>
        ) : (
          <nav className="flex items-center" style={{ position: 'relative', zIndex: 99999 }}>
            <Link to="/login" style={navLinkStyle}>
              GiriÅŸ
            </Link>
            <Link to="/register" style={{...navButtonStyle, background: '#4CAF50', marginLeft: '0.5rem'}}>
              KayÄ±t Ol
            </Link>
          </nav>
        )}
      </div>
    </header>
  );
};

export default Header;
