import React from 'react';
import { Link, useNavigate } from 'react-router-dom';

function Header({ isAuthenticated, onLogout }) {
  const handleClick = (e, path) => {
    e.preventDefault();
    window.location.href = path;
  };

  return (
    <header className="header" style={{ 
      position: 'sticky', 
      top: 0, 
      zIndex: 999999, 
      width: '100%',
      boxShadow: '0 2px 8px rgba(0,0,0,0.15)',
      background: 'linear-gradient(45deg, #4CAF50, #8BC34A)',
      padding: '1rem 0',
      color: 'white'
    }}>
      <div className="container header-content">
        <a href="/" 
          onClick={(e) => handleClick(e, '/')}
          className="logo" 
          style={{ 
            position: 'relative', 
            zIndex: 999999,
            color: 'white',
            textDecoration: 'none',
            fontSize: '1.8rem',
            fontWeight: 'bold',
            display: 'flex',
            alignItems: 'center'
          }}>
          <span>ðŸ¥—</span> Diyet Dostu
        </a>
        
        <div className="nav-links" style={{ 
          position: 'relative', 
          zIndex: 999999,
          display: 'flex',
          gap: '1.5rem'
        }}>
          {isAuthenticated ? (
            <div className="header-actions">
              <Link to="/sohbet" className="header-link">
                <div className="nav-icon">
                  <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8 10h.01M12 10h.01M16 10h.01M9 16H5a2 2 0 01-2-2V6a2 2 0 012-2h14a2 2 0 012 2v8a2 2 0 01-2 2h-5l-5 5v-5z" />
                  </svg>
                </div>
                <span>AI Asistan</span>
              </Link>
              <Link to="/profil" className="header-link">Profil</Link>
              <button onClick={onLogout} className="header-button">Ã‡Ä±kÄ±ÅŸ</button>
            </div>
          ) : (
            <>
              <a 
                href="/login" 
                onClick={(e) => handleClick(e, '/login')}
                className="nav-link" 
                style={{ 
                  position: 'relative', 
                  zIndex: 999999, 
                  cursor: 'pointer !important',
                  color: 'white',
                  fontWeight: 500,
                  padding: '0.5rem 1rem',
                  textDecoration: 'none'
                }}
              >
                GiriÅŸ
              </a>
              <a 
                href="/register" 
                onClick={(e) => handleClick(e, '/register')}
                className="nav-link" 
                style={{ 
                  position: 'relative', 
                  zIndex: 999999, 
                  cursor: 'pointer !important',
                  color: 'white',
                  fontWeight: 500,
                  padding: '0.5rem 1rem',
                  textDecoration: 'none'
                }}
              >
                KayÄ±t Ol
              </a>
            </>
          )}
        </div>
      </div>
    </header>
  );
}

export default Header;
