import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import ChatBubble from './ChatBubble';
import UserInfoForm from './UserInfoForm';
import DietPlanGenerator from './DietPlanGenerator';

const Chat = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [imageFile, setImageFile] = useState(null);
  const [imagePreview, setImagePreview] = useState(null);
  const [chatList, setChatList] = useState([]);
  const [activeChatId, setActiveChatId] = useState(null);
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const [showUserInfoForm, setShowUserInfoForm] = useState(false);
  const [showDietPlan, setShowDietPlan] = useState(false);
  const [userInfo, setUserInfo] = useState(null);
  const messagesEndRef = useRef(null);
  const textareaRef = useRef(null);

  useEffect(() => {
    fetchChatList();
    checkUserInfo();
  }, []);

  useEffect(() => {
    if (activeChatId) {
      fetchChatHistory();
    }
  }, [activeChatId]);

  useEffect(() => {
    scrollToBottom();
  }, [messages, showUserInfoForm, showDietPlan]);

  const checkUserInfo = async () => {
    try {
      const token = localStorage.getItem('token');
      
      if (!token) {
        console.error('Token bulunamadı');
        return;
      }
      
      const response = await axios.get('/api/user-info', {
        headers: { Authorization: `Bearer ${token}` }
      });
      
      if (response.data.userInfo) {
        setUserInfo(response.data.userInfo);
      } else {
        // Kullanıcı bilgisi yoksa formu göster
        // Gerçek uygulamada sohbet başlattıktan sonra göster
        // Şimdilik yorum satırı olarak bırakıyoruz
        // setShowUserInfoForm(true);
      }
    } catch (error) {
      console.error('Kullanıcı bilgisi kontrolü sırasında hata:', error);
    }
  };

  const fetchChatList = async () => {
    try {
      setIsLoading(true);
      const token = localStorage.getItem('token');
      
      if (!token) {
        console.error('Token bulunamadı');
        setIsLoading(false);
        return;
      }
      
      const response = await axios.get('/api/chat-list', {
        headers: { Authorization: `Bearer ${token}` }
      });
      
      // Sohbet listesini ayarla
      setChatList(response.data);
      
      // İlk sohbeti aktif olarak işaretle (eğer liste boş değilse)
      if (response.data.length > 0 && !activeChatId) {
        setActiveChatId(response.data[0].id);
        fetchChatHistory(response.data[0].id);
      } else if (response.data.length === 0) {
        // Sohbet listesi boşsa yeni bir sohbet oluştur
        createNewChat();
      }
    } catch (error) {
      console.error('Sohbet listesi alınırken hata:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const fetchChatHistory = async (chatId) => {
    try {
      setIsLoading(true);
      const token = localStorage.getItem('token');
      
      if (!token) {
        console.error('Token bulunamadı');
        setIsLoading(false);
        return;
      }
      
      const response = await axios.get(`/api/chat-history/${chatId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      
      // Başarılı yanıtta mesajları güncelle
      if (response.data.chatHistory && Array.isArray(response.data.chatHistory)) {
        setMessages(response.data.chatHistory);
      } else {
        // Boş mesaj listesi
        setMessages([]);
      }
    } catch (error) {
      console.error('Sohbet geçmişi alınırken hata:', error);
      setMessages([]);
    } finally {
      setIsLoading(false);
    }
  };

  const createNewChat = () => {
    try {
      setIsLoading(true);
      const token = localStorage.getItem('token');
      
      if (!token) {
        console.error('Token bulunamadı');
        setIsLoading(false);
        return;
      }
      
      // Yeni sohbet oluştur
      axios.post('/api/create-chat', {}, {
        headers: { Authorization: `Bearer ${token}` }
      })
      .then(response => {
        if (response.data && response.data.id) {
          // Yeni sohbeti listeye ekle
          const newChat = {
            id: response.data.id,
            title: response.data.title || 'Yeni Sohbet',
            lastMessage: new Date().toISOString()
          };
          
          setChatList(prevList => [...prevList, newChat]);
          setActiveChatId(response.data.id);
          
          // Yeni sohbetin geçmişini yükle (boş olacak)
          setMessages([{
            role: 'assistant',
            content: `Merhaba! 😊 Diyet Dostu'na hoş geldin!

Ben senin kişisel beslenme ve spor asistanınım. Bana istediğin konuda soru sorabilirsin.

Türk mutfağına uygun, kolay ve sağlıklı tarifler sunarak diyet hedeflerine ulaşmana yardımcı olacağım.

İşte sana yardımcı olabileceğim konular:
🥗 Türk mutfağına uygun düşük kalorili/yüksek proteinli tarifler
🏋️‍♀️ Kişiselleştirilmiş egzersiz planları
📸 Yemek fotoğraflarını analiz ederek besin değeri tahmini
📝 Haftalık beslenme programı oluşturma
📊 Kilo takibi ve gelişim analizi

Sormak istediğin bir şey var mı? Birlikte sağlıklı bir yaşam yolculuğuna başlayalım! 💪`
          }]);
        }
      })
      .catch(error => {
        console.error('Yeni sohbet oluşturulurken hata:', error);
      })
      .finally(() => {
        setIsLoading(false);
      });
    } catch (error) {
      console.error('Yeni sohbet oluşturulurken hata:', error);
      setIsLoading(false);
    }
  };

  const deleteChat = async (chatId) => {
    if (!window.confirm('Bu sohbeti silmek istediğinizden emin misiniz?')) {
      return;
    }
    
    try {
      setIsLoading(true);
      const token = localStorage.getItem('token');
      
      if (!token) {
        console.error('Token bulunamadı');
        setIsLoading(false);
        return;
      }
      
      // API'ya istek gönder
      const response = await axios.delete(`/api/delete-chat/${chatId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      
      // Başarılı yanıtta UI'ı güncelle
      if (response.data.success) {
        // Sohbet listesinden sil
        setChatList(prevList => prevList.filter(chat => chat.id !== chatId));
        
        // Eğer aktif sohbet siliniyorsa başka bir sohbete geç
        if (chatId === activeChatId) {
          const remainingChats = chatList.filter(chat => chat.id !== chatId);
          const nextChat = remainingChats.length > 0 ? remainingChats[0] : null;
          
          if (nextChat) {
            setActiveChatId(nextChat.id);
            // Bu sohbetin geçmişini yükle
            fetchChatHistory(nextChat.id);
          } else {
            // Hiç sohbet kalmadıysa yeni bir sohbet oluştur
            createNewChat();
          }
        }
        
        // Bildirim göster
        alert('Sohbet başarıyla silindi.');
      } else {
        alert('Sohbet silinirken bir hata oluştu.');
      }
    } catch (error) {
      console.error('Sohbet silinirken hata:', error);
      alert('Sohbet silinirken bir hata oluştu. Lütfen tekrar deneyin.');
    } finally {
      setIsLoading(false);
    }
  };

  const clearChatHistory = async () => {
    try {
      if (!window.confirm('Sohbet geçmişini temizlemek istediğinizden emin misiniz?')) {
        return;
      }
      
      setIsLoading(true);
      const token = localStorage.getItem('token');
      
      if (!token) {
        console.error('Token bulunamadı');
        setIsLoading(false);
        return;
      }
      
      // API'ya istek gönder
      const response = await axios.post('/api/clear-chat', {}, {
        headers: { Authorization: `Bearer ${token}` }
      });
      
      if (response.data.success) {
        // Başarılı yanıtta mesajları güncelle
        setMessages(response.data.chatHistory || [{
          role: 'assistant',
          content: `Merhaba! 😊 Diyet Dostu'na hoş geldin!

Ben senin kişisel beslenme ve spor asistanınım. Sohbet geçmişin temizlendi ve yeni bir konuşma başlatıldı.

Türk mutfağına uygun, kolay ve sağlıklı tarifler sunarak diyet hedeflerine ulaşmana yardımcı olacağım.

İşte sana yardımcı olabileceğim konular:
🥗 Türk mutfağına uygun düşük kalorili/yüksek proteinli tarifler
🏋️‍♀️ Kişiselleştirilmiş egzersiz planları
📸 Yemek fotoğraflarını analiz ederek besin değeri tahmini
📝 Haftalık beslenme programı oluşturma
📊 Kilo takibi ve gelişim analizi

Sormak istediğin bir şey var mı? Birlikte sağlıklı bir yaşam yolculuğuna başlayalım! 💪`
        }]);
        
        // Aktif sohbeti güncelle
        const updatedList = chatList.map(chat => {
          if (chat.id === activeChatId) {
            return { ...chat, title: 'Temizlenmiş Sohbet' };
          }
          return chat;
        });
        
        setChatList(updatedList);
        
        // Formları gizle
        setShowUserInfoForm(false);
        setShowDietPlan(false);
        
        // Başarı mesajı göster
        alert('Sohbet geçmişi başarıyla temizlendi.');
      } else {
        // Hata mesajı
        alert('Sohbet geçmişi temizlenirken bir hata oluştu.');
      }
    } catch (error) {
      console.error('Sohbet geçmişi temizlenirken hata:', error);
      // Hata durumunda kullanıcıyı bilgilendir
      alert('Sohbet geçmişi temizlenirken bir hata oluştu. Lütfen tekrar deneyin.');
    } finally {
      setIsLoading(false);
    }
  };

  const scrollToBottom = () => {
    if (messagesEndRef.current) {
      messagesEndRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSubmit(e);
    }
  };

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      setImageFile(file);
      const reader = new FileReader();
      reader.onloadend = () => {
        setImagePreview(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const clearImage = () => {
    setImageFile(null);
    setImagePreview(null);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const message = input.trim();
    
    if (!message && !imageFile) return;
    
    try {
      setIsLoading(true);
      const token = localStorage.getItem('token');
      
      if (!token) {
        console.error('Token bulunamadı');
        setMessages((prev) => [...prev, {
          role: 'assistant',
          content: 'Oturum süreniz dolmuş görünüyor. Lütfen tekrar giriş yapın.'
        }]);
        setIsLoading(false);
        return;
      }
      
      // Kullanıcı mesajını ekle
      const userMessage = {
        role: 'user',
        content: message
      };
      
      setMessages((prev) => [...prev, userMessage]);
      setInput('');
      
      // Özel komutları kontrol et
      if (message.toLowerCase().includes('kişisel bilgilerimi girmek istiyorum') ||
          message.toLowerCase().includes('diyet programı oluşturmak istiyorum') ||
          message.toLowerCase().includes('bilgilerimi güncellemek istiyorum')) {
        
        setTimeout(() => {
          setShowUserInfoForm(true);
          setIsLoading(false);
        }, 500);
        
        return;
      }
      
      if (message.toLowerCase().includes('diyet planımı göster') ||
          message.toLowerCase().includes('beslenme planımı göster') ||
          message.toLowerCase().includes('beslenme planı oluştur')) {
        
        if (!userInfo) {
          // Kullanıcı bilgisi yoksa bilgi formunu göster
          setTimeout(() => {
            setMessages((prev) => [...prev, {
              role: 'assistant',
              content: 'Diyet planı oluşturmak için öncelikle kişisel bilgilerinizi girmeniz gerekiyor. Bilgi formunu açıyorum...'
            }]);
            
            setTimeout(() => {
              setShowUserInfoForm(true);
              setIsLoading(false);
            }, 500);
          }, 500);
        } else {
          // Kullanıcı bilgisi varsa diyet planını göster
          setTimeout(() => {
            setMessages((prev) => [...prev, {
              role: 'assistant',
              content: 'İşte size özel hazırladığım beslenme planı:'
            }]);
            
            setTimeout(() => {
              setShowDietPlan(true);
              setIsLoading(false);
            }, 500);
          }, 500);
        }
        
        return;
      }
      
      // Eğer görsel varsa, farklı bir endpoint kullanarak görsel analizi yapın
      if (imageFile) {
        const formData = new FormData();
        formData.append('image', imageFile);
        
        try {
          console.log('Görsel analizi isteği gönderiliyor...');
          const response = await axios.post(
            '/api/analyze-image',
            formData,
            {
              headers: { 
                Authorization: `Bearer ${token}`,
                'Content-Type': 'multipart/form-data' 
              }
            }
          );
          
          console.log('Görsel analiz yanıtı:', response.data);
          
          // Analiz sonucunu göster (eğer socket.io ile otomatik gelmiyorsa)
          if (!response.data.message || response.data.message !== 'Görüntü analiz edildi') {
            // Mesaj yanıtı sadece bildirim içeriyorsa, birkaç saniye sonra sohbet geçmişini güncelle
            setTimeout(() => {
              fetchChatHistory(activeChatId);
            }, 1000);
          }
          
          // Görsel önizlemeyi temizle
          clearImage();
        } catch (imgError) {
          console.error('Görsel analiz hatası:', imgError);
          
          // Hata mesajını sohbete ekle
          setMessages((prev) => [...prev, {
            role: 'assistant',
            content: 'Görsel analiz edilirken bir hata oluştu. Lütfen daha sonra tekrar deneyin veya başka bir görsel yükleyin.'
          }]);
        }
      } else {
        // Görsel yoksa normal mesaj gönderimi yap
        try {
          console.log('Mesaj gönderiliyor:', message);
          console.log('Aktif sohbet ID:', activeChatId);
          
          const response = await axios.post(
            '/api/chat',
            {
              message,
              chatId: activeChatId
            },
            {
              headers: { Authorization: `Bearer ${token}` }
            }
          );
          
          console.log('API yanıtı:', response.data);
          
          // Asistan yanıtını ekle (cevap varsa)
          if (response.data.response) {
            const assistantMessage = {
              role: 'assistant',
              content: response.data.response
            };
            
            setMessages((prev) => [...prev, assistantMessage]);
          } else {
            // Yanıt yoksa, sohbet geçmişini güncellemek için bekle
            setTimeout(() => {
              fetchChatHistory(activeChatId);
            }, 1000);
          }
        } catch (chatError) {
          console.error('Mesaj gönderme hatası:', chatError);
          
          // Hata mesajını sohbete ekle ve daha açıklayıcı hata mesajları sağla
          let errorMessage = 'Üzgünüm, mesajınız gönderilemedi. Lütfen internet bağlantınızı kontrol edin veya daha sonra tekrar deneyin.';
          
          if (chatError.response) {
            // Sunucu yanıtı var, hata kodu ile birlikte
            console.error('Sunucu yanıt kodu:', chatError.response.status);
            console.error('Sunucu hata mesajı:', chatError.response.data);
            
            if (chatError.response.status === 401 || chatError.response.status === 403) {
              errorMessage = 'Oturum süreniz dolmuş görünüyor. Lütfen tekrar giriş yapın.';
            } else if (chatError.response.status === 404) {
              errorMessage = 'API endpoint bulunamadı. Sunucu yapılandırması kontrol edilmeli.';
            } else if (chatError.response.status === 500) {
              errorMessage = 'Sunucu hatası. Lütfen daha sonra tekrar deneyin.';
            }
          } else if (chatError.request) {
            // İstek yapıldı ama yanıt alınamadı
            console.error('Yanıt alınamadı:', chatError.request);
            errorMessage = 'Sunucuya bağlanılamıyor. Lütfen sunucunun çalıştığından emin olun.';
          }
          
          setMessages((prev) => [...prev, {
            role: 'assistant',
            content: errorMessage
          }]);
        }
      }
    } catch (error) {
      console.error('Mesaj gönderilirken genel hata:', error);
      
      // Hata mesajını sohbete ekleyin
      const errorMessage = {
        role: 'assistant',
        content: 'Üzgünüm, bir hata oluştu. Lütfen daha sonra tekrar deneyin.'
      };
      
      setMessages((prev) => [...prev, errorMessage]);
    } finally {
      setIsLoading(false);
      setTimeout(scrollToBottom, 100);
    }
  };

  const handleUserInfoSubmit = (formData) => {
    // Kullanıcı bilgilerini kaydet
    setUserInfo(formData);
    setShowUserInfoForm(false);
    
    // Kullanıcı bilgileri alındığında diyet planını göster
    setTimeout(() => {
      setMessages((prev) => [...prev, {
        role: 'assistant',
        content: `Teşekkürler! Bilgileriniz kaydedildi. 

Size özel bir beslenme planı hazırlıyorum. Bu plan, hedeflerinize, tercihlerinize ve ihtiyaçlarınıza göre özelleştirilmiştir.

İşte size özel beslenme planım:`
      }]);
      
      setTimeout(() => {
        setShowDietPlan(true);
      }, 1000);
    }, 500);
  };

  const toggleSidebar = () => {
    setSidebarOpen(!sidebarOpen);
  };

  // Eksik olduğu için not silme fonksiyonunu ekliyorum
  const deleteNote = async (noteId) => {
    try {
      const token = localStorage.getItem('token');
      
      if (!token) {
        console.error('Token bulunamadı');
        return;
      }
      
      const response = await axios.delete(`/api/delete-note/${noteId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      
      if (response.data.success) {
        // Notu listeden kaldır
        setMessages(messages.filter(msg => {
          // Not ID'si mesaj içinde tanımlanmışsa (data-note-id gibi bir özellik ile)
          return !(msg.type === 'note' && msg.noteId === noteId);
        }));
        
        // Bildirim göster
        alert('Not başarıyla silindi');
      } else {
        alert('Not silinirken bir hata oluştu');
      }
    } catch (error) {
      console.error('Not silinirken hata:', error);
      alert('Not silinirken bir hata oluştu. Lütfen tekrar deneyin.');
    }
  };

  return (
    <div className="flex h-[calc(100vh-64px)]">
      {/* Sol Kenar Çubuğu - Sohbet Listesi */}
      <div className={`chat-sidebar ${sidebarOpen ? 'open' : 'closed'}`}>
        <div className="p-4 border-b flex justify-between items-center">
          <h3 className="font-semibold text-gray-700">Sohbetlerim</h3>
          <button 
            onClick={createNewChat}
            className="bg-diet-green text-white p-2 rounded-lg hover:bg-green-600 transition-colors"
            title="Yeni sohbet başlat"
          >
            <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 4v16m8-8H4" />
            </svg>
          </button>
        </div>
        
        <div className="overflow-y-auto h-full">
          {chatList.map((chat) => (
            <div 
              key={chat.id}
              className={`p-3 border-b cursor-pointer hover:bg-gray-100 flex justify-between items-center ${chat.id === activeChatId ? 'bg-diet-green bg-opacity-10 border-l-4 border-diet-green' : ''}`}
            >
              <div className="flex-1" onClick={() => setActiveChatId(chat.id)}>
                <p className="font-medium text-gray-800">{chat.title}</p>
                <p className="text-xs text-gray-500">{chat.date}</p>
              </div>
              {chat.id !== 'current' && (
                <button
                  onClick={(e) => {
                    e.stopPropagation();
                    deleteChat(chat.id);
                  }}
                  className="text-gray-400 hover:text-red-500 p-1"
                  title="Sohbeti sil"
                >
                  <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" viewBox="0 0 20 20" fill="currentColor">
                    <path fillRule="evenodd" d="M9 2a1 1 0 00-.894.553L7.382 4H4a1 1 0 000 2v10a2 2 0 002 2h8a2 2 0 002-2V6a1 1 0 100-2h-3.382l-.724-1.447A1 1 0 0011 2H9zM7 8a1 1 0 012 0v6a1 1 0 11-2 0V8zm5-1a1 1 0 00-1 1v6a1 1 0 102 0V8a1 1 0 00-1-1z" clipRule="evenodd" />
                  </svg>
                </button>
              )}
            </div>
          ))}
        </div>
      </div>
      
      {/* Sohbet Bölümü */}
      <div className="flex-1 flex flex-col relative">
        {/* Mobil görünümde kenar çubuğu açma/kapama butonu */}
        <button 
          className="sidebar-toggle"
          onClick={toggleSidebar}
          title={sidebarOpen ? "Kenar çubuğunu kapat" : "Kenar çubuğunu aç"}
        >
          {sidebarOpen ? (
            <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M11 19l-7-7 7-7m8 14l-7-7 7-7" />
            </svg>
          ) : (
            <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 5l7 7-7 7M5 5l7 7-7 7" />
            </svg>
          )}
        </button>
        
        <div className="p-4 bg-diet-green text-white flex justify-between items-center">
          <div className="flex items-center">
            <div className="diyet-dostu-avatar">
              <div className="avatar-icon">
                <svg viewBox="0 0 24 24" fill="#4CAF50" xmlns="http://www.w3.org/2000/svg">
                  <circle cx="12" cy="12" r="10" fill="#e8f5e9" />
                  <path d="M17 8.5C17 6.01 14.99 4 12.5 4C10.01 4 8 6.01 8 8.5C8 11.99 12.5 16.5 12.5 16.5C12.5 16.5 17 11.99 17 8.5ZM12.5 9.5C11.95 9.5 11.5 9.05 11.5 8.5C11.5 7.95 11.95 7.5 12.5 7.5C13.05 7.5 13.5 7.95 13.5 8.5C13.5 9.05 13.05 9.5 12.5 9.5Z" fill="#4CAF50" />
                  <path d="M16 15H9C8.45 15 8 15.45 8 16C8 16.55 8.45 17 9 17H16C16.55 17 17 16.55 17 16C17 15.45 16.55 15 16 15Z" fill="#4CAF50" />
                  <path d="M12 20C16.42 20 20 16.42 20 12C20 7.58 16.42 4 12 4C7.58 4 4 7.58 4 12C4 16.42 7.58 20 12 20ZM12 2C17.52 2 22 6.48 22 12C22 17.52 17.52 22 12 22C6.48 22 2 17.52 2 12C2 6.48 6.48 2 12 2Z" fill="#4CAF50" />
                </svg>
              </div>
            </div>
            <div>
              <h2 className="text-xl font-semibold">Diyet Dostu 🥗</h2>
              <p className="text-sm">Sağlıklı yaşam asistanınız</p>
            </div>
          </div>
          <button 
            onClick={clearChatHistory}
            className="bg-white bg-opacity-20 hover:bg-opacity-30 text-white py-1 px-3 rounded-lg text-sm transition-colors duration-200"
            title="Sohbet geçmişini temizle"
          >
            Sohbeti Temizle
          </button>
        </div>
        
        <div className="flex-grow overflow-y-auto p-4 chat-messages">
          {messages.length === 0 ? (
            <div className="flex items-center justify-center h-full">
              <div className="text-center">
                <div className="text-4xl mb-4">👋</div>
                <p className="text-xl font-medium text-gray-700 mb-2">Diyet Dostu'na Hoş Geldin!</p>
                <p className="text-gray-500 mb-4">Beslenme, diyet veya fitness hakkında bir şeyler sor.</p>
              </div>
            </div>
          ) : (
            messages.map((msg, index) => (
              <div key={index}>
                {msg.type === 'note' ? (
                  <div className="note-container">
                    <ChatBubble message={msg} />
                    <button 
                      onClick={() => deleteNote(msg.noteId)}
                      className="delete-note-btn"
                      title="Notu sil"
                    >
                      <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" viewBox="0 0 20 20" fill="currentColor">
                        <path fillRule="evenodd" d="M9 2a1 1 0 00-.894.553L7.382 4H4a1 1 0 000 2v10a2 2 0 002 2h8a2 2 0 002-2V6a1 1 0 100-2h-3.382l-.724-1.447A1 1 0 0011 2H9zM7 8a1 1 0 012 0v6a1 1 0 11-2 0V8zm5-1a1 1 0 00-1 1v6a1 1 0 102 0V8a1 1 0 00-1-1z" clipRule="evenodd" />
                      </svg>
                    </button>
                  </div>
                ) : (
                  <ChatBubble message={msg} />
                )}
              </div>
            ))
          )}
          
          {isLoading && (
            <div className="flex items-center space-x-2 text-gray-500 p-4 bg-gray-100 rounded-lg max-w-[80%] mr-auto my-3 shadow-md loading-container">
              <div className="loader-container">
                <div className="food-icons">
                  <span className="icon apple">🍎</span>
                  <span className="icon broccoli">🥦</span>
                  <span className="icon avocado">🥑</span>
                </div>
                <p className="ml-2 font-medium">Diyet Dostu düşünüyor...</p>
              </div>
            </div>
          )}
          
          {/* Kullanıcı bilgi formu */}
          {showUserInfoForm && (
            <div className="my-4 p-2 bg-white rounded-lg shadow-lg">
              <UserInfoForm 
                onSubmit={handleUserInfoSubmit} 
                onCancel={() => setShowUserInfoForm(false)}
              />
            </div>
          )}
          
          {/* Diyet planı */}
          {showDietPlan && userInfo && (
            <div className="my-4 p-2 bg-white rounded-lg shadow-lg">
              <DietPlanGenerator userInfo={userInfo} />
            </div>
          )}
          
          <div ref={messagesEndRef} />
        </div>

        <div className="p-4 border-t">
          <form onSubmit={handleSubmit} className="flex items-end gap-2">
            <div className="flex-1 relative">
              <textarea
                value={input}
                onChange={(e) => setInput(e.target.value)}
                onKeyDown={handleKeyDown}
                ref={textareaRef}
                placeholder="Diyet Dostu'na bir soru sor..."
                className="w-full p-3 pr-12 border border-gray-300 rounded-lg resize-none focus:border-diet-green focus:ring-1 focus:ring-diet-green outline-none min-h-[50px] max-h-[200px]"
                rows="1"
              ></textarea>
              
              <label htmlFor="image-upload" className="absolute right-2 bottom-2 cursor-pointer text-gray-500 hover:text-diet-green p-1">
                <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 16l4.586-4.586a2 2 0 012.828 0L16 16m-2-2l1.586-1.586a2 2 0 012.828 0L20 14m-6-6h.01M6 20h12a2 2 0 002-2V6a2 2 0 00-2-2H6a2 2 0 00-2 2v12a2 2 0 002 2z" />
                </svg>
                <input
                  id="image-upload"
                  type="file"
                  accept="image/*"
                  onChange={handleImageUpload}
                  className="hidden"
                />
              </label>
            </div>
            
            <button
              type="submit"
              disabled={isLoading || (!input.trim() && !imageFile)}
              className={`p-3 rounded-lg ${isLoading || (!input.trim() && !imageFile) ? 'bg-gray-300 cursor-not-allowed' : 'bg-diet-green text-white hover:bg-green-600'} transition-colors`}
            >
              <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 5l7 7-7 7M5 5l7 7-7 7" />
              </svg>
            </button>
          </form>
          
          {imagePreview && (
            <div className="mt-2 relative">
              <img src={imagePreview} alt="Yüklenen görsel" className="h-20 object-contain rounded-md border border-gray-300" />
              <button 
                onClick={clearImage}
                className="absolute top-1 right-1 bg-red-500 text-white rounded-full p-1 hover:bg-red-600 transition-colors"
                title="Görseli kaldır"
              >
                <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                </svg>
              </button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Chat;
