import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';

const Login = () => {
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [testMode, setTestMode] = useState(false);
  const navigate = useNavigate();

  // Sayfa yüklendiğinde token kontrolü yap
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      navigate('/chat');
    }
  }, [navigate]);

  // Test modu aktifleştirildiğinde otomatik giriş yap
  useEffect(() => {
    if (testMode) {
      const submitButton = document.getElementById('login-submit');
      if (submitButton) {
        submitButton.click();
      }
    }
  }, [testMode]);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const loginWithTestUser = () => {
    setFormData({
      email: 'whodenur@gmail.com',
      password: 'test123'
    });
    setTestMode(true);
  };

  const handleTestLogin = () => {
    // Test kullanıcısı ile giriş işlemine devam et
    const token = `test_token_${Date.now()}`;
    localStorage.setItem('token', token);
    localStorage.setItem('userId', '1746606015392');
    navigate('/chat');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (testMode) {
      // Test modunda ise API'ye istek göndermeden doğrudan giriş yap
      handleTestLogin();
      return;
    }
    
    try {
      setError('');
      setLoading(true);
      
      const response = await axios.post('http://localhost:5000/api/login', formData);
      
      // JWT token'ı ve kullanıcı ID'sini localStorage'a kaydet
      localStorage.setItem('token', response.data.token);
      localStorage.setItem('userId', response.data.userId);
      
      // Kullanıcıyı sohbet sayfasına yönlendir
      navigate('/chat');
    } catch (error) {
      console.error('Login error:', error);
      
      if (error.response) {
        setError(error.response.data.message || 'Giriş başarısız');
      } else if (error.request) {
        setError('Sunucuya ulaşılamıyor. Lütfen daha sonra tekrar deneyin.');
      } else {
        setError('Giriş işlemi sırasında bir hata oluştu.');
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-diet-gray p-4 relative overflow-hidden">
      {/* Geri dönüş oku */}
      <Link to="/" className="absolute top-4 left-4 z-20 text-diet-green hover:text-diet-green-dark transition-colors">
        <div className="flex items-center">
          <svg xmlns="http://www.w3.org/2000/svg" className="h-8 w-8" fill="none" viewBox="0 0 24 24" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M10 19l-7-7m0 0l7-7m-7 7h18" />
          </svg>
          <span className="ml-1 font-medium">Anasayfa</span>
        </div>
      </Link>
      
      {/* Animasyonlu sebze ve meyveler */}
      <div className="food-animation-container">
        <div className="floating-food apple" style={{ top: '10%', left: '5%' }}>🍎</div>
        <div className="floating-food banana" style={{ top: '15%', right: '8%' }}>🍌</div>
        <div className="floating-food broccoli" style={{ bottom: '20%', left: '10%' }}>🥦</div>
        <div className="floating-food avocado" style={{ bottom: '15%', right: '15%' }}>🥑</div>
        <div className="floating-food orange" style={{ top: '35%', left: '20%' }}>🍊</div>
        <div className="floating-food tomato" style={{ top: '45%', right: '20%' }}>🍅</div>
        <div className="floating-food carrot" style={{ bottom: '30%', left: '25%' }}>🥕</div>
        <div className="floating-food pear" style={{ bottom: '40%', right: '10%' }}>🍐</div>
      </div>
      
      <div className="bg-white rounded-lg shadow-xl p-8 w-full max-w-md z-10 bg-opacity-95 auth-container">
        <div className="text-center mb-8">
          <h2 className="text-3xl font-bold text-diet-green">Diyet Dostu</h2>
          <p className="text-gray-600 mt-2">Sağlıklı yaşam asistanınız</p>
        </div>
        
        {error && (
          <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4 text-center">
            {error}
          </div>
        )}
        
        <form onSubmit={handleSubmit} className="space-y-6">
          <div>
            <label className="block text-gray-700 mb-2" htmlFor="email">
              E-posta
            </label>
            <input
              type="email"
              id="email"
              name="email"
              className="auth-input"
              placeholder="E-posta adresiniz"
              value={formData.email}
              onChange={handleChange}
              required
            />
          </div>
          <div>
            <label className="block text-gray-700 mb-2" htmlFor="password">
              Parola
            </label>
            <input
              type="password"
              id="password"
              name="password"
              className="auth-input"
              placeholder="Parolanız"
              value={formData.password}
              onChange={handleChange}
              required
            />
          </div>
          <button
            id="login-submit"
            type="submit"
            className="auth-button pulsing-button"
            disabled={loading}
          >
            {loading ? 'Giriş yapılıyor...' : 'Giriş Yap'}
          </button>
        </form>
        
        <div className="text-center mt-6">
          <p className="text-gray-600 mb-4">veya</p>
          <button
            onClick={loginWithTestUser}
            className="w-full bg-diet-blue text-white p-3 rounded-lg hover:bg-blue-600 transition-colors pulsing-button"
          >
            Test Kullanıcısı ile Giriş Yap
          </button>
        </div>
        
        <div className="text-center mt-6">
          <p className="text-gray-600">
            Hesabınız yok mu?{' '}
            <a href="/register" className="text-diet-green hover:underline">
              Kaydolun
            </a>
          </p>
        </div>
      </div>
    </div>
  );
};

export default Login;
