import React from 'react';

const ChatBubble = ({ message }) => {
  const { role, content } = message;
  const isUser = role === 'user';
  
  // Hastalık ve ilaç bilgisi içeren mesajları kontrol et
  const hasMedicalInfo = content && (
    content.includes('ÖNEMLİ SAĞLIK BİLGİLERİ') || 
    content.includes('hastalık') || 
    content.includes('ilaç') || 
    content.includes('rahatsızlık')
  );
  
  // HTML içeriğini güvenli bir şekilde render et
  const createMarkup = (text) => {
    return { __html: text
      .replace(/\n/g, '<br/>')
      .replace(/\*\*(.*?)\*\*/g, '<strong>$1</strong>')
      .replace(/\*(.*?)\*/g, '<em>$1</em>')
    };
  };
  
  return (
    <div className={`chat-message ${isUser ? 'user-message' : 'assistant-message'}`}>
      <div className="avatar-container">
        {isUser ? (
          <div className="user-avatar">
            <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="currentColor" className="w-full h-full">
              <path fillRule="evenodd" d="M18.685 19.097A9.723 9.723 0 0021.75 12c0-5.385-4.365-9.75-9.75-9.75S2.25 6.615 2.25 12a9.723 9.723 0 003.065 7.097A9.716 9.716 0 0012 21.75a9.716 9.716 0 006.685-2.653zm-12.54-1.285A7.486 7.486 0 0112 15a7.486 7.486 0 015.855 2.812A8.224 8.224 0 0112 20.25a8.224 8.224 0 01-5.855-2.438zM15.75 9a3.75 3.75 0 11-7.5 0 3.75 3.75 0 017.5 0z" clipRule="evenodd" />
            </svg>
          </div>
        ) : (
          <div className="assistant-avatar">
            <img src="/logo192.png" alt="Diyet Dostu" className="w-full h-full object-cover" onError={(e) => {
              e.target.onerror = null;
              e.target.src = "data:image/svg+xml,%3Csvg xmlns='http://www.w3.org/2000/svg' viewBox='0 0 24 24' fill='%234CAF50'%3E%3Cpath d='M12 2c5.514 0 10 4.486 10 10s-4.486 10-10 10-10-4.486-10-10 4.486-10 10-10zm0 18c4.411 0 8-3.589 8-8s-3.589-8-8-8-8 3.589-8 8 3.589 8 8 8zm4-8a4 4 0 11-8 0 4 4 0 018 0z'/%3E%3C/svg%3E";
            }} />
          </div>
        )}
      </div>
      <div className={`chat-bubble ${isUser ? 'user-bubble' : 'assistant-bubble'} ${hasMedicalInfo ? 'medical-info' : ''}`}>
        <div dangerouslySetInnerHTML={createMarkup(content)} />
      </div>
    </div>
  );
};

export default ChatBubble;
