import React from 'react';
// Link yerine doğrudan a etiketleri kullanacağız
// import { Link } from 'react-router-dom';

const Header = ({ isAuthenticated, onLogout }) => {
  // Headerı sayfanın her şeyinin üzerinde tutacak stil
  const headerStyle = {
    position: 'fixed',
    top: 0,
    left: 0,
    right: 0,
    zIndex: 999999,
    background: 'linear-gradient(45deg, #4CAF50, #8BC34A)',
    boxShadow: '0 4px 10px rgba(0,0,0,0.12)',
    width: '100%',
    padding: '1rem 0',
  };

  const containerStyle = {
    maxWidth: '1280px',
    margin: '0 auto',
    padding: '0 1rem',
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
  };

  const logoStyle = {
    display: 'flex',
    alignItems: 'center',
    fontWeight: 700,
    fontSize: '1.5rem',
    color: 'white',
    textDecoration: 'none',
    position: 'relative',
    zIndex: 999999,
  };

  const navStyle = {
    display: 'flex',
    alignItems: 'center',
    position: 'relative',
    zIndex: 999999,
  };

  const linkStyle = {
    color: 'white',
    fontWeight: 500,
    padding: '0.5rem 1rem',
    textDecoration: 'none',
    position: 'relative',
    zIndex: 999999,
  };

  const buttonStyle = {
    position: 'relative',
    zIndex: 999999,
    background: '#FF9800',
    color: 'white',
    padding: '0.5rem 1rem',
    borderRadius: '8px',
    fontWeight: 600,
    marginLeft: '1rem',
    border: 'none',
    cursor: 'pointer',
  };

  const handleLogoutClick = (e) => {
    e.preventDefault();
    onLogout();
  };

  // Link yerine normal <a> etiketleri kullanacağız
  return (
    <header style={headerStyle}>
      <div style={containerStyle}>
        <a href="/" style={logoStyle}>
          Diyet Dostu <span role="img" aria-label="salad">🥗</span>
        </a>
        
        {isAuthenticated ? (
          <nav style={navStyle}>
            <a href="/chat" style={linkStyle}>
              Sohbet
            </a>
            <a href="/profile" style={linkStyle}>
              Profil
            </a>
            <button 
              onClick={handleLogoutClick}
              style={buttonStyle}
            >
              Çıkış
            </button>
          </nav>
        ) : (
          <nav style={navStyle}>
            <a href="/login" style={linkStyle}>
              Giriş
            </a>
            <a href="/register" style={{...buttonStyle, background: '#4CAF50', textDecoration: 'none', marginLeft: '0.5rem'}}>
              Kayıt Ol
            </a>
          </nav>
        )}
      </div>
    </header>
  );
};

export default Header;
