import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';

const Login = ({ onLogin }) => {
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [testMode, setTestMode] = useState(false);
  const navigate = useNavigate();
  
  // Form değişikliklerini izle ve test moduna geçildiğinde otomatik giriş yap
  useEffect(() => {
    if (testMode && formData.email === 'whodenur@gmail.com' && formData.password === '123456') {
      // Direkt test kullanıcısıyla giriş yap
      console.log('Test kullanıcısı otomatik giriş yapıyor...');
      handleTestLogin();
    }
  }, [testMode, formData]);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  // Test kullanıcısı ile giriş - direkt chat'e gider
  const handleTestLogin = () => {
    console.log('Test modu aktif, direkt giriş yapılıyor');
    
    // Sahte token oluşturalım
    const token = "test_token_" + Date.now();
    const userId = "1746606015392"; // userData.json'daki kullanıcı ID'si
    
    // LocalStorage'a token ve userId'yi kaydet
    localStorage.setItem('token', token);
    localStorage.setItem('userId', userId);
    
    // Başarıyla giriş yaptık
    onLogin();
    
    // Direkt olarak chat sayfasına yönlendir
    setTimeout(() => {
      navigate('/chat');
    }, 500);
  };

  // Test kullanıcısı ile giriş - form doldurur, giriş butonuna tıklamak gerekir
  const loginWithTestUser = () => {
    setFormData({
      email: 'whodenur@gmail.com',
      password: '123456'
    });
    
    setTestMode(true);
    // Otomatik olarak formu gönder
    setTimeout(() => {
      document.querySelector('form button[type="submit"]').click();
    }, 1000);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    try {
      console.log('Giriş yapılıyor:', formData);
      
      // Eğer test modundaysak direkt olarak başarılı sayalım
      if (testMode) {
        handleTestLogin();
        return;
      }
      
      const response = await axios.post('http://localhost:5000/api/login', formData);
      console.log('Giriş başarılı:', response.data);
      
      // Token ve kullanıcı ID'sini kaydet
      localStorage.setItem('token', response.data.token);
      localStorage.setItem('userId', response.data.userId);
      
      // Başarıyla giriş yaptık
      onLogin();
      
      // Direkt olarak chat sayfasına yönlendir
      navigate('/chat');
    } catch (err) {
      console.error('Giriş hatası:', err);
      let errorMsg = 'Sunucuya bağlanılamadı, lütfen daha sonra tekrar deneyin';
      
      if (err.response) {
        errorMsg = err.response.data.message || 'Sunucu hatası';
        console.error('Sunucu hata detayı:', err.response.data);
      }
      
      setError(errorMsg);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="flex items-center justify-center min-h-[70vh]">
      <div className="auth-form w-full">
        <h2 className="text-2xl font-bold text-center mb-6 text-diet-green">Diyet Dostuna Hoş Geldin!</h2>
        
        {error && (
          <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
            {error}
          </div>
        )}
        
        <form onSubmit={handleSubmit}>
          <div className="mb-4">
            <label className="block text-gray-700 mb-2" htmlFor="email">
              E-posta
            </label>
            <input
              type="email"
              id="email"
              name="email"
              className="auth-input"
              placeholder="ornek@mail.com"
              value={formData.email}
              onChange={handleChange}
              required
            />
          </div>
          
          <div className="mb-6">
            <label className="block text-gray-700 mb-2" htmlFor="password">
              Parola
            </label>
            <input
              type="password"
              id="password"
              name="password"
              className="auth-input"
              placeholder="••••••••"
              value={formData.password}
              onChange={handleChange}
              required
            />
          </div>
          
          <button
            type="submit"
            className="auth-button flex justify-center items-center w-full"
            disabled={loading}
          >
            {loading ? (
              <svg className="animate-spin h-5 w-5 mr-3 text-white" viewBox="0 0 24 24">
                <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4" fill="none"></circle>
                <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
              </svg>
            ) : null}
            {loading ? 'Giriş Yapılıyor...' : 'Giriş Yap'}
          </button>
        </form>
        
        <div className="mt-6 text-center">
          <button
            onClick={loginWithTestUser}
            className="bg-blue-500 hover:bg-blue-600 text-white py-2 px-4 rounded-lg"
            disabled={loading}
          >
            Test Kullanıcısı ile Giriş Yap
          </button>
        </div>
        
        <div className="mt-6 text-center">
          <p className="text-gray-600">
            Hesabın yok mu?{' '}
            <Link to="/register" className="text-diet-green hover:underline">
              Hemen Kaydol
            </Link>
          </p>
        </div>
      </div>
    </div>
  );
};

export default Login;
