        setTimeout(() => {
          setShowUserInfoForm(true);
          setIsLoading(false);
        }, 500);
        
        return;
      }
      
      if (message.toLowerCase().includes('diyet planÄ±mÄ± gÃ¶ster') ||
          message.toLowerCase().includes('beslenme planÄ±mÄ± gÃ¶ster') ||
          message.toLowerCase().includes('beslenme planÄ± oluÅŸtur')) {
        
        if (!userInfo) {
          // KullanÄ±cÄ± bilgisi yoksa bilgi formunu gÃ¶ster
          setTimeout(() => {
            setMessages((prev) => [...prev, {
              role: 'assistant',
              content: 'Diyet planÄ± oluÅŸturmak iÃ§in Ã¶ncelikle kiÅŸisel bilgilerinizi girmeniz gerekiyor. Bilgi formunu aÃ§Ä±yorum...'
            }]);
            
            setTimeout(() => {
              setShowUserInfoForm(true);
              setIsLoading(false);
            }, 500);
          }, 500);
        } else {
          // KullanÄ±cÄ± bilgisi varsa diyet planÄ±nÄ± gÃ¶ster
          setTimeout(() => {
            setMessages((prev) => [...prev, {
              role: 'assistant',
              content: 'Ä°ÅŸte size Ã¶zel hazÄ±rladÄ±ÄŸÄ±m beslenme planÄ±:'
            }]);
            
            setTimeout(() => {
              setShowDietPlan(true);
              setIsLoading(false);
            }, 500);
          }, 500);
        }
        
        return;
      }
      
      // EÄŸer gÃ¶rsel varsa, farklÄ± bir endpoint kullanarak gÃ¶rsel analizi yapÄ±n
      if (imageFile) {
        const formData = new FormData();
        formData.append('image', imageFile);
        
        try {
          console.log('GÃ¶rsel analizi isteÄŸi gÃ¶nderiliyor...');
          const response = await axios.post(
            '/api/analyze-image',
            formData,
            {
              headers: { 
                Authorization: `Bearer ${token}`,
                'Content-Type': 'multipart/form-data' 
              }
            }
          );
          
          console.log('GÃ¶rsel analiz yanÄ±tÄ±:', response.data);
          
          // Analiz sonucunu gÃ¶ster (eÄŸer socket.io ile otomatik gelmiyorsa)
          if (!response.data.message || response.data.message !== 'GÃ¶rÃ¼ntÃ¼ analiz edildi') {
            // Mesaj yanÄ±tÄ± sadece bildirim iÃ§eriyorsa, birkaÃ§ saniye sonra sohbet geÃ§miÅŸini gÃ¼ncelle
            setTimeout(() => {
              fetchChatHistory(activeChatId);
            }, 1000);
          }
          
          // GÃ¶rsel Ã¶nizlemeyi temizle
          clearImage();
        } catch (imgError) {
          console.error('GÃ¶rsel analiz hatasÄ±:', imgError);
          
          // Hata mesajÄ±nÄ± sohbete ekle
          setMessages((prev) => [...prev, {
            role: 'assistant',
            content: 'GÃ¶rsel analiz edilirken bir hata oluÅŸtu. LÃ¼tfen daha sonra tekrar deneyin veya baÅŸka bir gÃ¶rsel yÃ¼kleyin.'
          }]);
        }
      } else {
        // GÃ¶rsel yoksa normal mesaj gÃ¶nderimi yap
        try {
          console.log('Mesaj gÃ¶nderiliyor:', message);
          console.log('Aktif sohbet ID:', activeChatId);
          
          const response = await axios.post(
            '/api/chat',
            {
              message,
              chatId: activeChatId
            },
            {
              headers: { Authorization: `Bearer ${token}` }
            }
          );
          
          console.log('API yanÄ±tÄ±:', response.data);
          
          // Asistan yanÄ±tÄ±nÄ± ekle (cevap varsa)
          if (response.data.response) {
            const assistantMessage = {
              role: 'assistant',
              content: response.data.response
            };
            
            setMessages((prev) => [...prev, assistantMessage]);
