            // YanÄ±t yoksa, sohbet geÃ§miÅŸini gÃ¼ncellemek iÃ§in bekle
            setTimeout(() => {
              fetchChatHistory(activeChatId);
            }, 1000);
          }
        } catch (chatError) {
          console.error('Mesaj gÃ¶nderme hatasÄ±:', chatError);
          
