          let errorMessage = 'ÃœzgÃ¼nÃ¼m, mesajÄ±nÄ±z gÃ¶nderilemedi. LÃ¼tfen internet baÄŸlantÄ±nÄ±zÄ± kontrol edin veya daha sonra tekrar deneyin.';
          
          if (chatError.response) {
            // Sunucu yanÄ±tÄ± var, hata kodu ile birlikte
            console.error('Sunucu yanÄ±t kodu:', chatError.response.status);
            console.error('Sunucu hata mesajÄ±:', chatError.response.data);
            
            if (chatError.response.status === 401 || chatError.response.status === 403) {
              errorMessage = 'Oturum sÃ¼reniz dolmuÅŸ gÃ¶rÃ¼nÃ¼yor. LÃ¼tfen tekrar giriÅŸ yapÄ±n.';
            } else if (chatError.response.status === 404) {
              errorMessage = 'API endpoint bulunamadÄ±. Sunucu yapÄ±landÄ±rmasÄ± kontrol edilmeli.';
            } else if (chatError.response.status === 500) {
              errorMessage = 'Sunucu hatasÄ±. LÃ¼tfen daha sonra tekrar deneyin.';
            }
          } else if (chatError.request) {
            // Ä°stek yapÄ±ldÄ± ama yanÄ±t alÄ±namadÄ±
            console.error('YanÄ±t alÄ±namadÄ±:', chatError.request);
            errorMessage = 'Sunucuya baÄŸlanÄ±lamÄ±yor. LÃ¼tfen sunucunun Ã§alÄ±ÅŸtÄ±ÄŸÄ±ndan emin olun.';
          }
          
          setMessages((prev) => [...prev, {
            role: 'assistant',
            content: errorMessage
          }]);
        }
      }
    } catch (error) {
      console.error('Mesaj gÃ¶nderilirken genel hata:', error);
      
      // Hata mesajÄ±nÄ± sohbete ekleyin
      const errorMessage = {
        role: 'assistant',
        content: 'ÃœzgÃ¼nÃ¼m, bir hata oluÅŸtu. LÃ¼tfen daha sonra tekrar deneyin.'
      };
      
      setMessages((prev) => [...prev, errorMessage]);
    } finally {
      setIsLoading(false);
      setTimeout(scrollToBottom, 100);
