  return (
    <div className="chat-page-container">
      {/* Sol Kenar Ã‡ubuÄŸu - Sohbet Listesi */}
      <div className={`sidebar ${sidebarOpen ? 'open' : 'closed'}`}>
        <div className="sidebar-header">
          <h3>Sohbetlerim</h3>
          <button 
            onClick={createNewChat}
            className="new-chat-button"
            title="Yeni sohbet baÅŸlat"
          >
            <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 4v16m8-8H4" />
            </svg>
          </button>
        </div>
        
        <div className="chat-list">
          {chatList.map((chat) => (
            <div 
              key={chat.id}
              className={`chat-item ${chat.id === activeChatId ? 'active' : ''}`}
              onClick={() => setActiveChatId(chat.id)}
            >
              <div className="chat-item-content">
                <p className="chat-item-title">{chat.title || 'Yeni Sohbet'}</p>
                <p className="chat-item-date">{chat.date || new Date().toLocaleDateString('tr-TR')}</p>
              </div>
              {chat.id !== 'current' && (
                <button
                  onClick={(e) => {
                    e.stopPropagation();
                    deleteChat(chat.id);
                  }}
                  className="delete-chat-button"
                  title="Sohbeti sil"
                >
                  <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" viewBox="0 0 20 20" fill="currentColor">
                    <path fillRule="evenodd" d="M9 2a1 1 0 00-.894.553L7.382 4H4a1 1 0 000 2v10a2 2 0 002 2h8a2 2 0 002-2V6a1 1 0 100-2h-3.382l-.724-1.447A1 1 0 0011 2H9zM7 8a1 1 0 012 0v6a1 1 0 11-2 0V8zm5-1a1 1 0 00-1 1v6a1 1 0 102 0V8a1 1 0 00-1-1z" clipRule="evenodd" />
                  </svg>
                </button>
              )}
            </div>
          ))}
        </div>
      </div>
      
      {/* Ana Sohbet AlanÄ± */}
      <div className="main-chat-area">
        {/* Mobil gÃ¶rÃ¼nÃ¼mde kenar Ã§ubuÄŸu aÃ§ma/kapama butonu */}
        <button 
          className="toggle-sidebar-button"
          onClick={toggleSidebar}
          title={sidebarOpen ? "Kenar Ã§ubuÄŸunu kapat" : "Kenar Ã§ubuÄŸunu aÃ§"}
        >
          {sidebarOpen ? (
            <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M11 19l-7-7 7-7m8 14l-7-7 7-7" />
            </svg>
          ) : (
            <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 5l7 7-7 7M5 5l7 7-7 7" />
            </svg>
          )}
        </button>
        
        <div className="chat-header">
          <div className="chat-header-content">
            <div className="assistant-profile">
              <div className="assistant-avatar">ðŸ¥—</div>
              <div className="assistant-info">
                <h2>Diyet Dostu</h2>
                <p>SaÄŸlÄ±klÄ± yaÅŸam asistanÄ±nÄ±z</p>
              </div>
            </div>
            <button 
              onClick={clearChatHistory}
              className="clear-button"
              title="Sohbet geÃ§miÅŸini temizle"
            >
              <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-1" viewBox="0 0 20 20" fill="currentColor">
                <path fillRule="evenodd" d="M9 2a1 1 0 00-.894.553L7.382 4H4a1 1 0 000 2v10a2 2 0 002 2h8a2 2 0 002-2V6a1 1 0 100-2h-3.382l-.724-1.447A1 1 0 0011 2H9zM7 8a1 1 0 012 0v6a1 1 0 11-2 0V8zm5-1a1 1 0 00-1 1v6a1 1 0 102 0V8a1 1 0 00-1-1z" clipRule="evenodd" />
              </svg>
              Sohbeti Temizle
            </button>
          </div>
        </div>
        
        <div className="messages-container">
          {messages.length === 0 ? (
            <div className="welcome-message">
              <div className="welcome-icon">ðŸ‘‹</div>
              <h2>Diyet Dostu'na HoÅŸ Geldin!</h2>
              <p>Beslenme, diyet veya fitness hakkÄ±nda bir ÅŸeyler sor.</p>
              <div className="example-questions">
                <button onClick={() => setInput("Kilo vermek iÃ§in nasÄ±l beslenmeliyim?")}>
                  Kilo vermek iÃ§in nasÄ±l beslenmeliyim?
                </button>
                <button onClick={() => setInput("BugÃ¼n ne kadar protein almalÄ±yÄ±m?")}>
                  BugÃ¼n ne kadar protein almalÄ±yÄ±m?
                </button>
                <button onClick={() => setInput("AkÅŸam yemeÄŸi iÃ§in saÄŸlÄ±klÄ± bir tarif Ã¶nerir misin?")}>
                  AkÅŸam yemeÄŸi iÃ§in saÄŸlÄ±klÄ± bir tarif Ã¶nerir misin?
                </button>
              </div>
            </div>
          ) : (
            <div className="messages-list">
              {messages.map((msg, index) => (
                <div key={index} className={`message-container ${msg.role}`}>
                  {msg.role === 'assistant' && (
                    <div className="avatar-container">
                      <div className="assistant-avatar">ðŸ¥—</div>
                    </div>
                  )}
                  <div className={`message ${msg.role === 'user' ? 'user-message' : 'assistant-message'}`}>
                    {msg.content}
                  </div>
                </div>
              ))}
            </div>
          )}
          
          {isLoading && (
            <div className="loading-message">
              <div className="loading-avatar">
                <div className="assistant-avatar">ðŸ¥—</div>
              </div>
              <div className="loading-animation">
                <div className="loading-dot"></div>
                <div className="loading-dot"></div>
                <div className="loading-dot"></div>
              </div>
            </div>
          )}
          
          {/* KullanÄ±cÄ± bilgi formu */}
          {showUserInfoForm && (
            <div className="user-info-form-container">
              <UserInfoForm 
                onSubmit={handleUserInfoSubmit} 
                onCancel={() => setShowUserInfoForm(false)}
              />
            </div>
          )}
          
          {/* Diyet planÄ± */}
          {showDietPlan && userInfo && (
            <div className="diet-plan-container">
              <DietPlanGenerator userInfo={userInfo} />
            </div>
          )}
          
          <div ref={messagesEndRef} />
        </div>

        <div className="input-container">
          <form onSubmit={handleSubmit} className="input-form">
            <div className="textarea-container">
              <textarea
                value={input}
                onChange={(e) => setInput(e.target.value)}
                onKeyDown={handleKeyDown}
                ref={textareaRef}
                placeholder="Diyet Dostu'na bir soru sor..."
                rows="1"
                className="chat-input"
              ></textarea>
              
              <label htmlFor="image-upload" className="upload-image-button">
                <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 16l4.586-4.586a2 2 0 012.828 0L16 16m-2-2l1.586-1.586a2 2 0 012.828 0L20 14m-6-6h.01M6 20h12a2 2 0 002-2V6a2 2 0 00-2-2H6a2 2 0 00-2 2v12a2 2 0 002 2z" />
                </svg>
                <input
                  id="image-upload"
                  type="file"
                  accept="image/*"
                  onChange={handleImageUpload}
                  className="hidden"
                />
              </label>
            </div>
            
            <button
              type="submit"
              disabled={isLoading || (!input.trim() && !imageFile)}
              className="send-button"
            >
              <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 5l7 7-7 7M5 5l7 7-7 7" />
              </svg>
            </button>
          </form>
          
          {imagePreview && (
            <div className="image-preview-container">
              <img src={imagePreview} alt="YÃ¼klenen gÃ¶rsel" className="image-preview" />
              <button 
                onClick={clearImage}
                className="remove-image-button"
                title="GÃ¶rseli kaldÄ±r"
              >
                <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                </svg>
              </button>
            </div>
          )}
        </div>
      </div>

      {/* API Key Modal */}
      {!keySubmitted ? (
        <div className="api-key-modal">
          <div className="api-key-container">
            <h2>Gemini API AnahtarÄ± Gerekli</h2>
            <p>Diyet Dostu AI ile sohbet etmek iÃ§in Gemini API anahtarÄ±nÄ±zÄ± girmeniz gerekiyor.</p>
            <form onSubmit={handleSubmitApiKey}>
              <input
                type="text"
                value={apiKey}
                onChange={(e) => setApiKey(e.target.value)}
                className="api-key-input"
                placeholder="Gemini API anahtarÄ±nÄ±z"
                required
              />
              <div className="api-key-actions">
                <label className="remember-checkbox">
                  <input type="checkbox" defaultChecked />
                  <span>Bu bilgisayarda hatÄ±rla</span>
                </label>
                <button type="submit" className="api-key-button">Devam Et</button>
              </div>
            </form>
          </div>
        </div>
      ) : null}
    </div>
  );
};

export default Chat;
