      <div className="container mx-auto flex justify-between items-center">
        <Link to="/" className="logo">
          Diyet Dostu <span className="logo-icon">ðŸ¥—</span>
        </Link>
        
        {isAuthenticated ? (
          <nav className="flex items-center">
            <Link to="/chat" className="px-4 py-2 font-medium text-gray-700 hover:text-diet-green">
              Sohbet
            </Link>
            <Link to="/profile" className="px-4 py-2 font-medium text-gray-700 hover:text-diet-green">
              Profil
            </Link>
            <button 
              onClick={onLogout}
              className="ml-4 px-4 py-2 bg-diet-orange text-white rounded-lg hover:bg-orange-600 transition-colors"
            >
              Ã‡Ä±kÄ±ÅŸ
            </button>
          </nav>
        ) : (
          <nav className="flex items-center">
            <Link to="/login" className="px-4 py-2 font-medium text-gray-700 hover:text-diet-green">
              GiriÅŸ
            </Link>
            <Link to="/register" className="ml-2 px-4 py-2 bg-diet-green text-white rounded-lg hover:bg-green-600 transition-colors">
              KayÄ±t Ol
            </Link>
          </nav>
        )}
      </div>
    </header>
  );
};

export default Header;
