import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ExerciseCard from './ExerciseCard';

const Profile = () => {
  const [userData, setUserData] = useState(null);
  const [activeTab, setActiveTab] = useState('bilgiler');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [newNote, setNewNote] = useState('');
  const [newWeight, setNewWeight] = useState('');
  const [diseases, setDiseases] = useState('');
  const [medications, setMedications] = useState('');
  const [healthInfoEditing, setHealthInfoEditing] = useState(false);

  useEffect(() => {
    fetchUserData();
  }, []);

  const fetchUserData = async () => {
    try {
      const token = localStorage.getItem('token');
      
      if (!token) {
        throw new Error('Oturum zaman aşımına uğradı. Lütfen tekrar giriş yapın.');
      }
      
      const response = await axios.get('http://localhost:5000/api/profile', {
        headers: { Authorization: `Bearer ${token}` }
      });
      
      setUserData(response.data);
      // Sağlık bilgilerini set et
      if (response.data.info) {
        setDiseases(response.data.info.diseases || '');
        setMedications(response.data.info.medications || '');
      }
    } catch (error) {
      console.error('Profil bilgileri yüklenirken hata:', error);
      setError('Profil bilgileri yüklenemedi. Lütfen daha sonra tekrar deneyin.');
    } finally {
      setLoading(false);
    }
  };

  const handleToggleExercise = async (exercise) => {
    try {
      const token = localStorage.getItem('token');
      
      if (!token) {
        throw new Error('Oturum zaman aşımına uğradı. Lütfen tekrar giriş yapın.');
      }
      
      // Egzersiz tamamlandı durumunu değiştir
      await axios.post(
        'http://localhost:5000/api/profile/mark-complete',
        {
          type: 'exercise',
          item: exercise,
          completed: !exercise.completed
        },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      
      // Profil verilerini güncelle
      fetchUserData();
    } catch (error) {
      console.error('Egzersiz durumu güncellenirken hata:', error);
    }
  };

  const handleToggleMeal = async (day, meal) => {
    try {
      const token = localStorage.getItem('token');
      
      if (!token) {
        throw new Error('Oturum zaman aşımına uğradı. Lütfen tekrar giriş yapın.');
      }
      
      // Yemek tamamlandı durumunu değiştir
      await axios.post(
        'http://localhost:5000/api/profile/mark-complete',
        {
          type: 'meal',
          day,
          meal: meal.name,
          completed: !meal.completed
        },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      
      // Profil verilerini güncelle
      fetchUserData();
    } catch (error) {
      console.error('Yemek durumu güncellenirken hata:', error);
    }
  };

  const handleToggleNote = async (note) => {
    try {
      const token = localStorage.getItem('token');
      
      if (!token) {
        throw new Error('Oturum zaman aşımına uğradı. Lütfen tekrar giriş yapın.');
      }
      
      // Not tamamlandı durumunu değiştir
      await axios.post(
        'http://localhost:5000/api/profile/mark-complete',
        {
          type: 'note',
          note,
          completed: !note.completed
        },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      
      // Profil verilerini güncelle
      fetchUserData();
    } catch (error) {
      console.error('Not durumu güncellenirken hata:', error);
    }
  };

  const handleAddNote = async (e) => {
    e.preventDefault();
    
    if (!newNote.trim()) return;
    
    try {
      const token = localStorage.getItem('token');
      
      if (!token) {
        throw new Error('Oturum zaman aşımına uğradı. Lütfen tekrar giriş yapın.');
      }
      
      // Not ekle
      await axios.post(
        'http://localhost:5000/api/profile/update',
        {
          type: 'note',
          text: newNote
        },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      
      // Not alanını temizle
      setNewNote('');
      
      // Profil verilerini güncelle
      fetchUserData();
    } catch (error) {
      console.error('Not eklenirken hata:', error);
    }
  };

  const handleAddWeight = async (e) => {
    e.preventDefault();
    
    if (!newWeight.trim()) return;
    
    try {
      const token = localStorage.getItem('token');
      
      if (!token) {
        throw new Error('Oturum zaman aşımına uğradı. Lütfen tekrar giriş yapın.');
      }
      
      // Kilo ekle
      await axios.post(
        'http://localhost:5000/api/profile/update',
        {
          type: 'weight',
          weight: parseFloat(newWeight)
        },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      
      // Kilo alanını temizle
      setNewWeight('');
      
      // Profil verilerini güncelle
      fetchUserData();
    } catch (error) {
      console.error('Kilo eklenirken hata:', error);
    }
  };

  const handleSaveHealthInfo = async (e) => {
    e.preventDefault();
    
    try {
      const token = localStorage.getItem('token');
      
      if (!token) {
        throw new Error('Oturum zaman aşımına uğradı. Lütfen tekrar giriş yapın.');
      }
      
      // Sağlık bilgilerini güncelle
      await axios.post(
        'http://localhost:5000/api/profile/update',
        {
          type: 'health',
          diseases: diseases,
          medications: medications
        },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      
      // Düzenleme modunu kapat
      setHealthInfoEditing(false);
      
      // Profil verilerini güncelle
      fetchUserData();
    } catch (error) {
      console.error('Sağlık bilgileri güncellenirken hata:', error);
    }
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center min-h-[60vh]">
        <div className="text-center">
          <div className="w-16 h-16 border-4 border-diet-green border-t-transparent rounded-full animate-spin mx-auto mb-4"></div>
          <p className="text-gray-600">Profil bilgileri yükleniyor...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mx-auto max-w-md mt-10">
        {error}
      </div>
    );
  }

  if (!userData) {
    return (
      <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mx-auto max-w-md mt-10">
        Kullanıcı verileri bulunamadı.
      </div>
    );
  }

  // Sekme butonları
  const renderTabs = () => (
    <div className="flex flex-wrap border-b mb-6">
      <button
        onClick={() => setActiveTab('bilgiler')}
        className={`px-4 py-2 font-medium ${activeTab === 'bilgiler' ? 'text-diet-green border-b-2 border-diet-green' : 'text-gray-500'}`}
      >
        Kişisel Bilgiler
      </button>
      <button
        onClick={() => setActiveTab('kilo')}
        className={`px-4 py-2 font-medium ${activeTab === 'kilo' ? 'text-diet-green border-b-2 border-diet-green' : 'text-gray-500'}`}
      >
        Kilo Takibi
      </button>
      <button
        onClick={() => setActiveTab('beslenme')}
        className={`px-4 py-2 font-medium ${activeTab === 'beslenme' ? 'text-diet-green border-b-2 border-diet-green' : 'text-gray-500'}`}
      >
        Beslenme Planı
      </button>
      <button
        onClick={() => setActiveTab('egzersiz')}
        className={`px-4 py-2 font-medium ${activeTab === 'egzersiz' ? 'text-diet-green border-b-2 border-diet-green' : 'text-gray-500'}`}
      >
        Egzersiz Planı
      </button>
      <button
        onClick={() => setActiveTab('saglik')}
        className={`px-4 py-2 font-medium ${activeTab === 'saglik' ? 'text-diet-green border-b-2 border-diet-green' : 'text-gray-500'}`}
      >
        Sağlık Bilgileri
      </button>
      <button
        onClick={() => setActiveTab('notlar')}
        className={`px-4 py-2 font-medium ${activeTab === 'notlar' ? 'text-diet-green border-b-2 border-diet-green' : 'text-gray-500'}`}
      >
        Notlarım
      </button>
    </div>
  );

  // Sağlık Bilgileri Sekmesi
  const renderHealthTab = () => (
    <div className="bg-white rounded-lg shadow-md p-6">
      <h2 className="text-2xl font-bold text-diet-green mb-4">Sağlık Bilgileri</h2>
      <p className="text-gray-600 mb-4">Bu bilgiler, size özel diyet ve egzersiz önerilerinde dikkate alınır.</p>
      
      {healthInfoEditing ? (
        <form onSubmit={handleSaveHealthInfo} className="space-y-4">
          <div>
            <label className="block text-gray-700 font-medium mb-2">Rahatsızlıklar / Hastalıklar</label>
            <textarea
              value={diseases}
              onChange={(e) => setDiseases(e.target.value)}
              placeholder="Diyabet, hipertansiyon, çölyak, laktoz intoleransı vb."
              className="w-full p-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-diet-green"
              rows="3"
            ></textarea>
            <p className="text-sm text-gray-500 mt-1">Bilinen tüm rahatsızlıklarınızı veya hastalıklarınızı yazın.</p>
          </div>
          
          <div>
            <label className="block text-gray-700 font-medium mb-2">Kullandığınız İlaçlar</label>
            <textarea
              value={medications}
              onChange={(e) => setMedications(e.target.value)}
              placeholder="İlaç adı, dozaj bilgisi..."
              className="w-full p-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-diet-green"
              rows="3"
            ></textarea>
            <p className="text-sm text-gray-500 mt-1">Düzenli kullandığınız ilaçları ve dozaj bilgilerini yazın.</p>
          </div>
          
          <div className="flex space-x-2 pt-2">
            <button
              type="submit"
              className="bg-diet-green text-white py-2 px-4 rounded-md hover:bg-green-600 transition"
            >
              Kaydet
            </button>
            <button
              type="button"
              onClick={() => setHealthInfoEditing(false)}
              className="bg-gray-200 text-gray-800 py-2 px-4 rounded-md hover:bg-gray-300 transition"
            >
              İptal
            </button>
          </div>
        </form>
      ) : (
        <div className="space-y-6">
          <div>
            <h3 className="text-lg font-semibold text-diet-green mb-2">Rahatsızlıklar / Hastalıklar</h3>
            <div className="bg-gray-50 p-4 rounded-md border border-gray-200">
              {userData.info?.diseases ? (
                <p className="text-gray-700">{userData.info.diseases}</p>
              ) : (
                <p className="text-gray-500 italic">Henüz bilgi girilmemiş</p>
              )}
            </div>
          </div>
          
          <div>
            <h3 className="text-lg font-semibold text-diet-green mb-2">Kullandığınız İlaçlar</h3>
            <div className="bg-gray-50 p-4 rounded-md border border-gray-200">
              {userData.info?.medications ? (
                <p className="text-gray-700">{userData.info.medications}</p>
              ) : (
                <p className="text-gray-500 italic">Henüz bilgi girilmemiş</p>
              )}
            </div>
          </div>
          
          <button
            onClick={() => setHealthInfoEditing(true)}
            className="mt-4 bg-diet-green text-white py-2 px-4 rounded-md hover:bg-green-600 transition"
          >
            Düzenle
          </button>
        </div>
      )}
    </div>
  );

  return (
    <div className="max-w-5xl mx-auto">
      {renderTabs()}
      
      {activeTab === 'bilgiler' && (
        <div>
          <div className="profile-card">
            <h3 className="text-xl font-semibold mb-4">Kişisel Bilgiler</h3>
            <div className="grid grid-cols-2 gap-4">
              <div>
                <p className="text-gray-600">Yaş</p>
                <p className="font-medium">{userData.info.age}</p>
              </div>
              <div>
                <p className="text-gray-600">Boy</p>
                <p className="font-medium">{userData.info.height} cm</p>
              </div>
              <div>
                <p className="text-gray-600">Hedef</p>
                <p className="font-medium capitalize">{userData.info.goal}</p>
              </div>
            </div>
          </div>
          
          <div className="profile-card">
            <div className="flex justify-between items-center mb-4">
              <h3 className="text-xl font-semibold">Kilo Takibi</h3>
              <form onSubmit={handleAddWeight} className="flex items-center">
                <input
                  type="number"
                  placeholder="Kg"
                  className="w-20 p-2 border rounded-l-lg focus:outline-none focus:ring-2 focus:ring-diet-green"
                  value={newWeight}
                  onChange={(e) => setNewWeight(e.target.value)}
                  step="0.1"
                  min="30"
                  max="300"
                  required
                />
                <button
                  type="submit"
                  className="bg-diet-green text-white p-2 rounded-r-lg"
                >
                  Ekle
                </button>
              </form>
            </div>
            
            {userData.weightHistory && userData.weightHistory.length > 0 ? (
              <div className="overflow-x-auto">
                <table className="w-full text-left">
                  <thead>
                    <tr className="bg-gray-100">
                      <th className="p-2">Tarih</th>
                      <th className="p-2">Kilo (kg)</th>
                    </tr>
                  </thead>
                  <tbody>
                    {userData.weightHistory.slice().reverse().map((entry, index) => (
                      <tr key={index} className="border-b">
                        <td className="p-2">{new Date(entry.date).toLocaleDateString('tr-TR')}</td>
                        <td className="p-2 font-medium">{entry.weight} kg</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            ) : (
              <p className="text-gray-600">Henüz kilo kaydı yok.</p>
            )}
          </div>
        </div>
      )}
      
      {activeTab === 'beslenme' && (
        <div>
          <div className="profile-card">
            <h3 className="text-xl font-semibold mb-4">Haftalık Beslenme Planı</h3>
            
            {userData.dietPlan && Object.keys(userData.dietPlan).length > 0 ? (
              Object.entries(userData.dietPlan).map(([week, days]) => (
                <div key={week} className="mb-6">
                  <h4 className="font-medium text-lg mb-2 text-diet-green">{week.replace(/([A-Z])/g, ' $1').trim()}</h4>
                  
                  {Object.entries(days).map(([day, meals]) => (
                    <div key={day} className="mb-4">
                      <h5 className="font-medium capitalize mb-2">{day}</h5>
                      
                      <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
                        {Object.entries(meals).map(([mealTime, meal]) => (
                          <div key={mealTime} className="bg-white p-3 rounded-lg shadow-sm flex justify-between items-center">
                            <div>
                              <p className="text-gray-600 capitalize">{mealTime}</p>
                              <p className="font-medium">{meal.name}</p>
                            </div>
                            <div 
                              className={`complete-button ${meal.completed ? 'completed' : ''}`}
                              onClick={() => handleToggleMeal(day, meal)}
                            >
                              {meal.completed && (
                                <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={2} stroke="currentColor" className="w-4 h-4">
                                  <path strokeLinecap="round" strokeLinejoin="round" d="M4.5 12.75l6 6 9-13.5" />
                                </svg>
                              )}
                            </div>
                          </div>
                        ))}
                      </div>
                    </div>
                  ))}
                </div>
              ))
            ) : (
              <p className="text-gray-600">Henüz beslenme planı yok. Diyetistin ile sohbet ederek bir plan oluşturabilirsin.</p>
            )}
          </div>
        </div>
      )}
      
      {activeTab === 'egzersiz' && (
        <div>
          <div className="profile-card">
            <h3 className="text-xl font-semibold mb-4">Haftalık Egzersiz Planı</h3>
            
            {userData.exercisePlan && Object.keys(userData.exercisePlan).length > 0 ? (
              Object.entries(userData.exercisePlan).map(([week, days]) => (
                <div key={week} className="mb-6">
                  <h4 className="font-medium text-lg mb-2 text-diet-green">{week.replace(/([A-Z])/g, ' $1').trim()}</h4>
                  
                  {Object.entries(days).map(([day, exercises]) => (
                    <div key={day} className="mb-4">
                      <h5 className="font-medium capitalize mb-2">{day}</h5>
                      
                      <div>
                        {exercises.map((exercise, index) => (
                          <ExerciseCard
                            key={index}
                            exercise={exercise}
                            onToggleComplete={handleToggleExercise}
                          />
                        ))}
                      </div>
                    </div>
                  ))}
                </div>
              ))
            ) : (
              <p className="text-gray-600">Henüz egzersiz planı yok. Diyetistin ile sohbet ederek bir plan oluşturabilirsin.</p>
            )}
          </div>
        </div>
      )}
      
      {activeTab === 'notlar' && (
        <div>
          <div className="profile-card">
            <div className="flex justify-between items-center mb-4">
              <h3 className="text-xl font-semibold">Notlarım</h3>
              <form onSubmit={handleAddNote} className="flex items-center">
                <input
                  type="text"
                  placeholder="Yeni not..."
                  className="w-48 p-2 border rounded-l-lg focus:outline-none focus:ring-2 focus:ring-diet-green"
                  value={newNote}
                  onChange={(e) => setNewNote(e.target.value)}
                  required
                />
                <button
                  type="submit"
                  className="bg-diet-green text-white p-2 rounded-r-lg"
                >
                  Ekle
                </button>
              </form>
            </div>
            
            {userData.notes && userData.notes.length > 0 ? (
              <div className="space-y-2">
                {userData.notes.slice().reverse().map((note, index) => (
                  <div 
                    key={index} 
                    className="p-3 border rounded-lg flex justify-between items-center"
                  >
                    <p className={`${note.completed ? 'line-through text-gray-500' : ''}`}>
                      {note.text}
                    </p>
                    <div 
                      className={`complete-button ${note.completed ? 'completed' : ''}`}
                      onClick={() => handleToggleNote(note)}
                    >
                      {note.completed && (
                        <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={2} stroke="currentColor" className="w-4 h-4">
                          <path strokeLinecap="round" strokeLinejoin="round" d="M4.5 12.75l6 6 9-13.5" />
                        </svg>
                      )}
                    </div>
                  </div>
                ))}
              </div>
            ) : (
              <p className="text-gray-600">Henüz not eklenmemiş.</p>
            )}
          </div>
        </div>
      )}
      
      {activeTab === 'saglik' && renderHealthTab()}
    </div>
  );
};

export default Profile;
