import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import ChatBubble from './ChatBubble';
import UserInfoForm from './UserInfoForm';
import DietPlanGenerator from './DietPlanGenerator';

const Chat = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [imageFile, setImageFile] = useState(null);
  const [imagePreview, setImagePreview] = useState(null);
  const [chatList, setChatList] = useState([]);
  const [activeChatId, setActiveChatId] = useState(null);
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const [showUserInfoForm, setShowUserInfoForm] = useState(false);
  const [showDietPlan, setShowDietPlan] = useState(false);
  const [userInfo, setUserInfo] = useState(null);
  const messagesEndRef = useRef(null);
  const textareaRef = useRef(null);

  useEffect(() => {
    fetchChatList();
    checkUserInfo();
  }, []);

  useEffect(() => {
    if (activeChatId) {
      fetchChatHistory();
    }
  }, [activeChatId]);

  useEffect(() => {
    scrollToBottom();
  }, [messages, showUserInfoForm, showDietPlan]);

  const checkUserInfo = async () => {
    try {
      const token = localStorage.getItem('token');
      
      if (!token) {
        console.error('Token bulunamadÄ±');
        return;
      }
      
      const response = await axios.get('http://localhost:5000/api/user-info', {
        headers: { Authorization: `Bearer ${token}` }
      });
      
      if (response.data.userInfo) {
        setUserInfo(response.data.userInfo);
      } else {
        // KullanÄ±cÄ± bilgisi yoksa formu gÃ¶ster
        // GerÃ§ek uygulamada sohbet baÅŸlattÄ±ktan sonra gÃ¶ster
        // Åimdilik yorum satÄ±rÄ± olarak bÄ±rakÄ±yoruz
        // setShowUserInfoForm(true);
      }
    } catch (error) {
      console.error('KullanÄ±cÄ± bilgisi kontrolÃ¼ sÄ±rasÄ±nda hata:', error);
    }
  };

  const fetchChatList = async () => {
    try {
      const token = localStorage.getItem('token');
      
      if (!token) {
        console.error('Token bulunamadÄ±');
        return;
      }
      
      // Bu endpoint'i sunucu tarafÄ±nda oluÅŸturmanÄ±z gerekecek
      // Ancak ÅŸimdilik simÃ¼le ediyoruz
      // GerÃ§ek uygulamada: const response = await axios.get('http://localhost:5000/api/chat-list', {...});
      
      // SimÃ¼le edilmiÅŸ sohbet listesi
      const simulatedChats = [
        { id: 'current', title: 'Mevcut Sohbet', date: new Date().toLocaleDateString() },
        { id: 'diet-plan', title: 'Diyet PlanÄ± OluÅŸturma', date: '01.05.2025' },
        { id: 'recipe-ideas', title: 'Yemek Tarifleri', date: '28.04.2025' },
        { id: 'weight-loss', title: 'Kilo Verme Tavsiyeleri', date: '25.04.2025' }
      ];
      
      setChatList(simulatedChats);
      // Ä°lk sohbeti aktif sohbet olarak ayarla
      setActiveChatId('current');
      
    } catch (error) {
      console.error('Sohbet listesi yÃ¼klenirken hata:', error);
    }
  };

  const fetchChatHistory = async () => {
    try {
      const token = localStorage.getItem('token');
      
      if (!token) {
        console.error('Token bulunamadÄ±');
        return;
      }
      
      const response = await axios.get('http://localhost:5000/api/chat-history', {
        headers: { Authorization: `Bearer ${token}` }
      });
      
      if (response.data.chatHistory) {
        setMessages(response.data.chatHistory);
      }
    } catch (error) {
      console.error('Sohbet geÃ§miÅŸi yÃ¼klenirken hata:', error);
    }
  };

  const createNewChat = () => {
    // Yeni bir sohbet ID'si oluÅŸtur
    const newChatId = 'chat-' + Date.now();
    
    // Sohbet listesine ekle
    const newChat = {
      id: newChatId,
      title: 'Yeni Sohbet',
      date: new Date().toLocaleDateString()
    };
    
    setChatList([newChat, ...chatList]);
    setActiveChatId(newChatId);
    setMessages([{
      role: 'assistant',
      content: `Merhaba! ğŸ˜Š Diyet Dostu'na hoÅŸ geldin!

Ben senin kiÅŸisel beslenme ve spor asistanÄ±nÄ±m. Sohbet geÃ§miÅŸin temizlendi ve yeni bir konuÅŸma baÅŸlatÄ±ldÄ±.

TÃ¼rk mutfaÄŸÄ±na uygun, kolay ve saÄŸlÄ±klÄ± tarifler sunarak diyet hedeflerine ulaÅŸmana yardÄ±mcÄ± olacaÄŸÄ±m.

Ä°ÅŸte sana yardÄ±mcÄ± olabileceÄŸim konular:
ğŸ¥— TÃ¼rk mutfaÄŸÄ±na uygun dÃ¼ÅŸÃ¼k kalorili/yÃ¼ksek proteinli tarifler
ğŸ‹ï¸â€â™€ï¸ KiÅŸiselleÅŸtirilmiÅŸ egzersiz planlarÄ±
ğŸ“¸ Yemek fotoÄŸraflarÄ±nÄ± analiz ederek besin deÄŸeri tahmini
ğŸ“ HaftalÄ±k beslenme programÄ± oluÅŸturma
ğŸ“Š Kilo takibi ve geliÅŸim analizi

Sormak istediÄŸin bir ÅŸey var mÄ±? Birlikte saÄŸlÄ±klÄ± bir yaÅŸam yolculuÄŸuna baÅŸlayalÄ±m! ğŸ’ª`
    }]);
    
    // EÄŸer kullanÄ±cÄ± bilgileri yoksa formu gÃ¶ster
    if (!userInfo) {
      setTimeout(() => {
        setMessages(prev => [...prev, {
          role: 'assistant',
          content: `Sana daha iyi yardÄ±mcÄ± olabilmem iÃ§in bazÄ± bilgilere ihtiyacÄ±m var. KiÅŸiselleÅŸtirilmiÅŸ diyet tavsiyeleri ve beslenme planÄ± oluÅŸturmamÄ± istersen "kiÅŸisel bilgilerimi girmek istiyorum" yazabilirsin.`
        }]);
      }, 1000);
    }
  };

  const deleteChat = async (chatId) => {
    if (!window.confirm('Bu sohbeti silmek istediÄŸinizden emin misiniz? Bu iÅŸlem geri alÄ±namaz.')) {
      return;
    }
    
    try {
      setIsLoading(true);
      const token = localStorage.getItem('token');
      
      if (!token) {
        console.error('Token bulunamadÄ±');
        setIsLoading(false);
        return;
      }
      
      // API Ã§aÄŸrÄ±sÄ±nÄ± yap
      const response = await axios.delete(`http://localhost:5000/api/delete-chat/${chatId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      
      if (response.data.success) {
        // Sohbeti listeden kaldÄ±r
        setChatList(chatList.filter(chat => chat.id !== chatId));
        
        // Silinecek sohbet aktif sohbetse baÅŸka bir sohbeti aktif yap
        if (chatId === activeChatId) {
          // Silinecek sohbet dÄ±ÅŸÄ±ndaki ilk sohbeti bul
          const nextChat = chatList.find(chat => chat.id !== chatId);
          
          if (nextChat) {
            setActiveChatId(nextChat.id);
            // Bu sohbetin geÃ§miÅŸini yÃ¼kle
            fetchChatHistory(nextChat.id);
          } else {
            // HiÃ§ sohbet kalmadÄ±ysa yeni bir sohbet oluÅŸtur
