import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import ChatBubble from './ChatBubble';

const Chat = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [imageFile, setImageFile] = useState(null);
  const [imagePreview, setImagePreview] = useState(null);
  const [chatList, setChatList] = useState([]);
  const [activeChatId, setActiveChatId] = useState(null);
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const messagesEndRef = useRef(null);
  const textareaRef = useRef(null);

  useEffect(() => {
    fetchChatList();
  }, []);

  useEffect(() => {
    if (activeChatId) {
      fetchChatHistory();
    }
  }, [activeChatId]);

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const fetchChatList = async () => {
    try {
      const token = localStorage.getItem('token');
      
      if (!token) {
        console.error('Token bulunamadı');
        return;
      }
      
      // Bu endpoint'i sunucu tarafında oluşturmanız gerekecek
      // Ancak şimdilik simüle ediyoruz
      // Gerçek uygulamada: const response = await axios.get('http://localhost:5000/api/chat-list', {...});
      
      // Simüle edilmiş sohbet listesi
      const simulatedChats = [
        { id: 'current', title: 'Mevcut Sohbet', date: new Date().toLocaleDateString() },
        { id: 'diet-plan', title: 'Diyet Planı Oluşturma', date: '01.05.2025' },
        { id: 'recipe-ideas', title: 'Yemek Tarifleri', date: '28.04.2025' },
        { id: 'weight-loss', title: 'Kilo Verme Tavsiyeleri', date: '25.04.2025' }
      ];
      
      setChatList(simulatedChats);
      // İlk sohbeti aktif sohbet olarak ayarla
      setActiveChatId('current');
      
    } catch (error) {
      console.error('Sohbet listesi yüklenirken hata:', error);
    }
  };

  const fetchChatHistory = async () => {
    try {
      const token = localStorage.getItem('token');
      
      if (!token) {
        console.error('Token bulunamadı');
        return;
      }
      
      const response = await axios.get('http://localhost:5000/api/chat-history', {
        headers: { Authorization: `Bearer ${token}` }
      });
      
      if (response.data.chatHistory) {
        setMessages(response.data.chatHistory);
      }
    } catch (error) {
      console.error('Sohbet geçmişi yüklenirken hata:', error);
    }
  };

  const createNewChat = () => {
    // Yeni bir sohbet ID'si oluştur
    const newChatId = 'chat-' + Date.now();
    
    // Sohbet listesine ekle
    const newChat = {
      id: newChatId,
      title: 'Yeni Sohbet',
      date: new Date().toLocaleDateString()
    };
    
    setChatList([newChat, ...chatList]);
    setActiveChatId(newChatId);
    setMessages([{
      role: 'assistant',
      content: `Merhaba! 😊 Yeni bir sohbete başladınız. Size nasıl yardımcı olabilirim?`
    }]);
  };

  const deleteChat = async (chatId) => {
    if (!window.confirm('Bu sohbeti silmek istediğinizden emin misiniz? Bu işlem geri alınamaz.')) {
      return;
    }
    
    try {
      const token = localStorage.getItem('token');
      
      if (!token) {
        console.error('Token bulunamadı');
        return;
      }
      
      // API çağrısını yap
      await axios.delete(`http://localhost:5000/api/delete-chat/${chatId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      
      // Silinecek sohbet aktif sohbetse başka bir sohbeti aktif yap
      if (chatId === activeChatId) {
        // Silinecek sohbet dışındaki ilk sohbeti bul
        const nextChat = chatList.find(chat => chat.id !== chatId);
        
        if (nextChat) {
          setActiveChatId(nextChat.id);
          // Bu sohbetin geçmişini yükle
          // Gerçek uygulamada API'dan yüklenecek, şimdilik temiz bir başlangıç yapalım
          setMessages([{
            role: 'assistant',
            content: `Hoş geldiniz! Size nasıl yardımcı olabilirim?`
          }]);
        } else {
          // Hiç sohbet kalmadıysa yeni bir sohbet oluştur
          createNewChat();
        }
      }
      
      // Sohbeti listeden kaldır
      setChatList(chatList.filter(chat => chat.id !== chatId));
      
    } catch (error) {
      console.error('Sohbet silinirken hata:', error);
      alert('Sohbet silinirken bir hata oluştu. Lütfen tekrar deneyin.');
    }
  };

  const clearChatHistory = async () => {
    try {
      if (!window.confirm('Sohbet geçmişini temizlemek istediğinizden emin misiniz?')) {
        return;
      }
      
      setIsLoading(true);
      const token = localStorage.getItem('token');
      
      if (!token) {
        console.error('Token bulunamadı');
        return;
      }
      
      // API'ya istek gönder
      // Gerçek bir API yanıtı bekleyene kadar, hemen UI'ı güncelleyelim
      setMessages([{
        role: 'assistant',
        content: `Merhaba! 😊 Sohbetiniz temizlendi. Size nasıl yardımcı olabilirim?`
      }]);
      
      // Aktif sohbeti güncelle
      const updatedList = chatList.map(chat => {
        if (chat.id === activeChatId) {
          return { ...chat, title: 'Temizlenmiş Sohbet' };
        }
        return chat;
      });
      
      setChatList(updatedList);
      
      // API isteği gönder
      const response = await axios.post('http://localhost:5000/api/clear-chat', {}, {
        headers: { Authorization: `Bearer ${token}` }
      });
      
      // API yanıtına göre tekrar güncelle
      if (response.data.chatHistory) {
        setMessages(response.data.chatHistory);
      }
      
    } catch (error) {
      console.error('Sohbet geçmişi temizlenirken hata:', error);
      // Hata durumunda kullanıcıyı bilgilendir
      setMessages(prev => [...prev, {
        role: 'assistant',
        content: 'Üzgünüm, sohbet geçmişi temizlenirken bir hata oluştu.'
      }]);
    } finally {
      setIsLoading(false);
    }
  };

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSubmit(e);
    }
  };

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      setImageFile(file);
      const reader = new FileReader();
      reader.onloadend = () => {
        setImagePreview(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const clearImage = () => {
    setImageFile(null);
    setImagePreview(null);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const message = input.trim();
    
    if (!message && !imageFile) return;
    
    try {
      setIsLoading(true);
      const token = localStorage.getItem('token');
      
      if (!token) {
        console.error('Token bulunamadı');
        return;
      }
      
      // Kullanıcı mesajını ekleyin
      const userMessage = {
        role: 'user',
        content: message
      };
      
      setMessages((prev) => [...prev, userMessage]);
      setInput('');
      
      // Eğer görsel varsa, farklı bir endpoint kullanarak görsel analizi yapın
      if (imageFile) {
        const formData = new FormData();
        formData.append('image', imageFile);
        
        await axios.post(
          'http://localhost:5000/api/analyze-image',
          formData,
          {
            headers: {
              Authorization: `Bearer ${token}`,
              'Content-Type': 'multipart/form-data'
            }
          }
        );
        
        clearImage();
      } else {
        // Normal metin mesajı gönderme
        const response = await axios.post('http://localhost:5000/api/chat',
          { message },
          { headers: { Authorization: `Bearer ${token}` } }
        );
        
        // Asistan yanıtını ekleyin (eğer yanıt mevcutsa)
        if (response.data.response) {
          const assistantMessage = {
            role: 'assistant',
            content: response.data.response
          };
          
          setMessages((prev) => [...prev, assistantMessage]);
        }

        // İlk mesaj ise sohbet başlığını güncelle
        if (messages.length <= 1) {
          const updatedList = chatList.map(chat => {
            if (chat.id === activeChatId) {
              // İlk 20 karakteri başlık olarak kullan
              return { ...chat, title: message.substring(0, 20) + (message.length > 20 ? '...' : '') };
            }
            return chat;
          });
          
          setChatList(updatedList);
        }
      }
    } catch (error) {
      console.error('Mesaj gönderilirken hata:', error);
      
      // Hata mesajını sohbete ekleyin
      const errorMessage = {
        role: 'assistant',
        content: 'Üzgünüm, bir hata oluştu. Lütfen daha sonra tekrar deneyin.'
      };
      
      setMessages((prev) => [...prev, errorMessage]);
    } finally {
      setIsLoading(false);
      setTimeout(scrollToBottom, 100);
    }
  };

  const toggleSidebar = () => {
    setSidebarOpen(!sidebarOpen);
  };

  return (
    <div className="flex h-[calc(100vh-64px)]">
      {/* Sol Kenar Çubuğu - Sohbet Listesi */}
      <div className={`chat-sidebar ${sidebarOpen ? 'open' : 'closed'}`}>
        <div className="p-4 border-b flex justify-between items-center">
          <h3 className="font-semibold text-gray-700">Sohbetlerim</h3>
          <button 
            onClick={createNewChat}
            className="bg-diet-green text-white p-2 rounded-lg hover:bg-green-600 transition-colors"
            title="Yeni sohbet başlat"
          >
            <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 4v16m8-8H4" />
            </svg>
          </button>
        </div>
        
        <div className="overflow-y-auto h-full">
          {chatList.map((chat) => (
            <div 
              key={chat.id}
              className={`p-3 border-b cursor-pointer hover:bg-gray-100 flex justify-between items-center ${chat.id === activeChatId ? 'bg-diet-green bg-opacity-10 border-l-4 border-diet-green' : ''}`}
            >
              <div className="flex-1" onClick={() => setActiveChatId(chat.id)}>
                <p className="font-medium text-gray-800">{chat.title}</p>
                <p className="text-xs text-gray-500">{chat.date}</p>
              </div>
              {chat.id !== 'current' && (
                <button
                  onClick={(e) => {
                    e.stopPropagation();
                    deleteChat(chat.id);
                  }}
                  className="text-gray-400 hover:text-red-500 p-1"
                  title="Sohbeti sil"
                >
                  <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" viewBox="0 0 20 20" fill="currentColor">
                    <path fillRule="evenodd" d="M9 2a1 1 0 00-.894.553L7.382 4H4a1 1 0 000 2v10a2 2 0 002 2h8a2 2 0 002-2V6a1 1 0 100-2h-3.382l-.724-1.447A1 1 0 0011 2H9zM7 8a1 1 0 012 0v6a1 1 0 11-2 0V8zm5-1a1 1 0 00-1 1v6a1 1 0 102 0V8a1 1 0 00-1-1z" clipRule="evenodd" />
                  </svg>
                </button>
              )}
            </div>
          ))}
        </div>
      </div>
      
      {/* Sohbet Bölümü */}
      <div className="flex-1 flex flex-col relative">
        {/* Mobil görünümde kenar çubuğu açma/kapama butonu */}
        <button 
          className="sidebar-toggle"
          onClick={toggleSidebar}
          title={sidebarOpen ? "Kenar çubuğunu kapat" : "Kenar çubuğunu aç"}
        >
          {sidebarOpen ? (
            <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M11 19l-7-7 7-7m8 14l-7-7 7-7" />
            </svg>
          ) : (
            <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 5l7 7-7 7M5 5l7 7-7 7" />
            </svg>
          )}
        </button>
        
        <div className="p-4 bg-diet-green text-white flex justify-between items-center">
          <div className="flex items-center">
            <div className="diyet-dostu-avatar">
              <div className="avatar-icon">
                <svg viewBox="0 0 24 24" fill="#4CAF50" xmlns="http://www.w3.org/2000/svg">
                  <circle cx="12" cy="12" r="10" fill="#e8f5e9" />
                  <path d="M17 8.5C17 6.01 14.99 4 12.5 4C10.01 4 8 6.01 8 8.5C8 11.99 12.5 16.5 12.5 16.5C12.5 16.5 17 11.99 17 8.5ZM12.5 9.5C11.95 9.5 11.5 9.05 11.5 8.5C11.5 7.95 11.95 7.5 12.5 7.5C13.05 7.5 13.5 7.95 13.5 8.5C13.5 9.05 13.05 9.5 12.5 9.5Z" fill="#4CAF50" />
                  <path d="M16 15H9C8.45 15 8 15.45 8 16C8 16.55 8.45 17 9 17H16C16.55 17 17 16.55 17 16C17 15.45 16.55 15 16 15Z" fill="#4CAF50" />
                  <path d="M12 20C16.42 20 20 16.42 20 12C20 7.58 16.42 4 12 4C7.58 4 4 7.58 4 12C4 16.42 7.58 20 12 20ZM12 2C17.52 2 22 6.48 22 12C22 17.52 17.52 22 12 22C6.48 22 2 17.52 2 12C2 6.48 6.48 2 12 2Z" fill="#4CAF50" />
                </svg>
              </div>
            </div>
            <div>
              <h2 className="text-xl font-semibold">Diyet Dostu 🥗</h2>
              <p className="text-sm">Sağlıklı yaşam asistanınız</p>
            </div>
          </div>
          <button 
            onClick={clearChatHistory}
            className="bg-white bg-opacity-20 hover:bg-opacity-30 text-white py-1 px-3 rounded-lg text-sm transition-colors duration-200"
            title="Sohbet geçmişini temizle"
          >
            Sohbeti Temizle
          </button>
        </div>
        
        <div className="flex-grow overflow-y-auto p-4 chat-messages">
          {messages.length === 0 ? (
            <div className="flex items-center justify-center h-full">
              <div className="text-center">
                <div className="text-4xl mb-4">👋</div>
                <p className="text-xl font-medium text-gray-700 mb-2">Diyet Dostu'na Hoş Geldin!</p>
                <p className="text-gray-500 mb-4">Beslenme, diyet veya fitness hakkında bir şeyler sor.</p>
              </div>
            </div>
          ) : (
            messages.map((msg, index) => (
              <ChatBubble 
                key={index}
                message={msg}
              />
            ))
          )}
          
          {isLoading && (
            <div className="flex items-center space-x-2 text-gray-500 p-4 bg-gray-100 rounded-lg max-w-[80%] mr-auto my-3 shadow-md loading-container">
              <div className="loader-container">
                <div className="food-icons">
                  <span className="icon apple">🍎</span>
                  <span className="icon broccoli">🥦</span>
                  <span className="icon avocado">🥑</span>
                </div>
                <p className="ml-2 font-medium">Diyet Dostu düşünüyor...</p>
              </div>
            </div>
          )}
          
          <div ref={messagesEndRef} />
        </div>

        <div className="p-4 border-t">
          <form onSubmit={handleSubmit} className="flex items-end gap-2">
            <div className="flex-1 relative">
              <textarea
                value={input}
                onChange={(e) => setInput(e.target.value)}
                placeholder="Bir mesaj yaz..."
                className="w-full p-3 border rounded-lg resize-none focus:outline-none focus:ring-2 focus:ring-diet-green"
                rows={1}
                ref={textareaRef}
                onKeyDown={handleKeyDown}
              />
              {imagePreview && (
                <div className="relative inline-block mt-2">
                  <img
                    src={imagePreview}
                    alt="Preview"
                    className="h-20 rounded border"
                  />
                  <button
                    type="button"
                    className="absolute -top-2 -right-2 bg-red-500 text-white rounded-full w-6 h-6 flex items-center justify-center"
                    onClick={clearImage}
                  >
                    ×
                  </button>
                </div>
              )}
            </div>
            
            <div className="flex items-center gap-2">
              <label className="cursor-pointer text-diet-green hover:text-diet-green-dark">
                <input
                  type="file"
                  accept="image/*"
                  className="hidden"
                  onChange={handleImageUpload}
                />
                <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 16l4.586-4.586a2 2 0 012.828 0L16 16m-2-2l1.586-1.586a2 2 0 012.828 0L20 14m-6-6h.01M6 20h12a2 2 0 002-2V6a2 2 0 00-2-2H6a2 2 0 00-2 2v12a2 2 0 002 2z" />
                </svg>
              </label>
              
              <button
                type="submit"
                disabled={!input.trim() && !imageFile}
                className={`p-3 rounded-full ${
                  !input.trim() && !imageFile
                    ? 'bg-gray-300 text-gray-500 cursor-not-allowed'
                    : 'bg-diet-green text-white hover:bg-diet-green-dark'
                }`}
              >
