import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import ChatBubble from './ChatBubble';
import ImageUploader from './ImageUploader';
import io from 'socket.io-client';

const Chat = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [socket, setSocket] = useState(null);
  const [showImageUploader, setShowImageUploader] = useState(false);
  const messagesEndRef = useRef(null);

  // Socket.io baÄŸlantÄ±sÄ±
  useEffect(() => {
    const newSocket = io('http://localhost:5000');
    setSocket(newSocket);
    
    const userId = localStorage.getItem('userId');
    if (userId) {
      newSocket.emit('join', { userId });
    }

    // Yeni mesajlarÄ± dinle
    newSocket.on('message', (message) => {
      setMessages((prev) => [...prev, message]);
    });

    // Socket.io baÄŸlantÄ±sÄ±nÄ± kapat
    return () => {
      newSocket.disconnect();
    };
  }, []);

  // Sohbet geÃ§miÅŸini yÃ¼kle
  useEffect(() => {
    const fetchChatHistory = async () => {
      try {
        const token = localStorage.getItem('token');
        
        if (!token) return;
        
        const response = await axios.get('http://localhost:5000/api/chat-history', {
          headers: { Authorization: `Bearer ${token}` }
        });
        
        if (response.data.chatHistory) {
          setMessages(response.data.chatHistory);
        } else {
          // KullanÄ±cÄ± ilk kez sohbet ediyorsa karÅŸÄ±lama mesajÄ±
          setMessages([
            { 
              role: 'assistant', 
              content: `Merhaba! ðŸ˜Š Diyet Dostu'na hoÅŸ geldin!

Ben senin kiÅŸisel beslenme ve spor asistanÄ±nÄ±m. TÃ¼rk mutfaÄŸÄ±na uygun, kolay ve saÄŸlÄ±klÄ± tarifler sunarak diyet hedeflerine ulaÅŸmana yardÄ±mcÄ± olacaÄŸÄ±m.

Sana daha iyi hizmet verebilmem iÃ§in biraz kendinden bahseder misin?
â€¢ YaÅŸÄ±n kaÃ§?
â€¢ Boyun ve kilon nedir?
â€¢ Hedefin nedir? (Kilo vermek, kilo almak, kas kazanmak, saÄŸlÄ±klÄ± beslenmek vb.)
â€¢ Herhangi bir besin alerjin veya Ã¶zel diyet tercÃ¼hin var mÄ±? (vejetaryen, glÃ¼tensiz, vb.)

Ä°ÅŸte sana yardÄ±mcÄ± olabileceÄŸim konular:
ðŸ¥— TÃ¼rk mutfaÄŸÄ±na uygun dÃ¼ÅŸÃ¼k kalorili/yÃ¼ksek proteinli tarifler
ðŸ‹ï¸â€â™€ï¸ KiÅŸiselleÅŸtirilmiÅŸ egzersiz planlarÄ±
ðŸ“¸ Yemek fotoÄŸraflarÄ±nÄ± analiz ederek besin deÄŸeri tahmini
ðŸ“ HaftalÄ±k beslenme programÄ± oluÅŸturma
ðŸ“Š Kilo takibi ve geliÅŸim analizi

Hadi birlikte saÄŸlÄ±klÄ± bir yaÅŸam yolculuÄŸuna baÅŸlayalÄ±m! ðŸ’ª` 
            }
          ]);
        }
      } catch (error) {
        console.error('Sohbet geÃ§miÅŸi yÃ¼klenirken hata:', error);
      }
    };

    fetchChatHistory();
  }, []);

  // Otomatik kaydÄ±rma
  useEffect(() => {
    if (messagesEndRef.current) {
      messagesEndRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  }, [messages]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!input.trim()) return;
    
    // KullanÄ±cÄ± mesajÄ±nÄ± ekran Ã¼zerinde gÃ¶ster
    const userMessage = { role: 'user', content: input };
    setMessages((prev) => [...prev, userMessage]);
    
    setInput('');
    setIsLoading(true);
    
    try {
      const token = localStorage.getItem('token');
      
      if (!token) {
        throw new Error('Oturum zaman aÅŸÄ±mÄ±na uÄŸradÄ±. LÃ¼tfen tekrar giriÅŸ yapÄ±n.');
      }
      
      // Asistan cevabÄ±nÄ± al
      await axios.post('http://localhost:5000/api/chat', 
        { message: input },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      
      // Socket.io ile yanÄ±t gelecek, burada bekleme yapmÄ±yoruz
    } catch (error) {
      console.error('Mesaj gÃ¶nderilirken hata:', error);
      setMessages((prev) => [
        ...prev, 
        { 
          role: 'assistant', 
          content: 'ÃœzgÃ¼nÃ¼m, bir hata oluÅŸtu. LÃ¼tfen daha sonra tekrar deneyin.'
        }
      ]);
    } finally {
      setIsLoading(false);
    }
  };

  const handleImageUpload = async (imageFile) => {
    setShowImageUploader(false);
    
    // YÃ¼kleniyor mesajÄ±
    setMessages((prev) => [
      ...prev, 
      { role: 'user', content: 'FotoÄŸraf yÃ¼klendi:', image: URL.createObjectURL(imageFile) }
    ]);
    
    setIsLoading(true);
    
    try {
      const token = localStorage.getItem('token');
      
      if (!token) {
        throw new Error('Oturum zaman aÅŸÄ±mÄ±na uÄŸradÄ±. LÃ¼tfen tekrar giriÅŸ yapÄ±n.');
      }
      
      // FormData oluÅŸtur
      const formData = new FormData();
      formData.append('image', imageFile);
      
      // GÃ¶rÃ¼ntÃ¼ analiz endpoint'ine gÃ¶nder
      await axios.post(
        'http://localhost:5000/api/analyze-image', 
        formData,
        { 
          headers: { 
            Authorization: `Bearer ${token}`,
            'Content-Type': 'multipart/form-data'
          } 
        }
      );
      
      // Socket.io ile yanÄ±t gelecek
    } catch (error) {
      console.error('GÃ¶rÃ¼ntÃ¼ analizi sÄ±rasÄ±nda hata:', error);
      setMessages((prev) => [
        ...prev, 
        { 
          role: 'assistant', 
          content: 'ÃœzgÃ¼nÃ¼m, gÃ¶rÃ¼ntÃ¼ analiz edilirken bir hata oluÅŸtu. LÃ¼tfen daha sonra tekrar deneyin.'
        }
      ]);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="chat-container">
      <div className="bg-white rounded-lg shadow-md overflow-hidden flex flex-col h-[70vh]">
        <div className="p-4 bg-diet-green text-white">
          <h2 className="text-xl font-semibold">Diyet Dostu ðŸ¥—</h2>
          <p className="text-sm">SaÄŸlÄ±klÄ± yaÅŸam asistanÄ±nÄ±z</p>
        </div>
        
        <div className="flex-grow overflow-y-auto p-4">
          {messages.map((message, index) => (
            <ChatBubble 
              key={index} 
              message={message.content}
              image={message.image}
              isUser={message.role === 'user'} 
            />
          ))}
          
          {isLoading && (
            <div className="flex items-center space-x-2 text-gray-500 p-3 bg-gray-100 rounded-lg max-w-[80%] mr-auto">
              <div className="flex space-x-1">
                <div className="w-2 h-2 rounded-full bg-diet-green animate-bounce" style={{ animationDelay: '0ms' }}></div>
                <div className="w-2 h-2 rounded-full bg-diet-green animate-bounce" style={{ animationDelay: '300ms' }}></div>
                <div className="w-2 h-2 rounded-full bg-diet-green animate-bounce" style={{ animationDelay: '600ms' }}></div>
              </div>
              <p>Diyet Dostu yanÄ±t yazÄ±yor...</p>
            </div>
          )}
          
          <div ref={messagesEndRef} />
        </div>
        
        {showImageUploader ? (
          <ImageUploader 
            onUpload={handleImageUpload} 
            onCancel={() => setShowImageUploader(false)}
          />
        ) : (
          <form onSubmit={handleSubmit} className="p-4 border-t">
            <div className="flex space-x-2">
              <button
                type="button"
                onClick={() => setShowImageUploader(true)}
                className="p-2 bg-diet-blue text-white rounded-full hover:bg-blue-500 transition-colors"
              >
                <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={1.5} stroke="currentColor" className="w-6 h-6">
                  <path strokeLinecap="round" strokeLinejoin="round" d="M2.25 15.75l5.159-5.159a2.25 2.25 0 013.182 0l5.159 5.159m-1.5-1.5l1.409-1.409a2.25 2.25 0 013.182 0l2.909 2.909m-18 3.75h16.5a1.5 1.5 0 001.5-1.5V6a1.5 1.5 0 00-1.5-1.5H3.75A1.5 1.5 0 002.25 6v12a1.5 1.5 0 001.5 1.5zm10.5-11.25h.008v.008h-.008V8.25zm.375 0a.375.375 0 11-.75 0 .375.375 0 01.75 0z" />
                </svg>
              </button>
              
              <input
                type="text"
                className="message-input flex-grow"
                placeholder="Bir mesaj yazÄ±n..."
                value={input}
                onChange={(e) => setInput(e.target.value)}
                disabled={isLoading}
              />
              
              <button
                type="submit"
                className="send-button"
                disabled={isLoading || !input.trim()}
