import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import ChatBubble from './ChatBubble';
import ImageUploader from './ImageUploader';
import io from 'socket.io-client';

const Chat = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [socket, setSocket] = useState(null);
  const [showImageUploader, setShowImageUploader] = useState(false);
  const messagesEndRef = useRef(null);

  // Socket.io bağlantısı
  useEffect(() => {
    const newSocket = io('http://localhost:5000');
    setSocket(newSocket);
    
    const userId = localStorage.getItem('userId');
    if (userId) {
      newSocket.emit('join', { userId });
    }

    // Yeni mesajları dinle
    newSocket.on('message', (message) => {
      setMessages((prev) => [...prev, message]);
    });

    // Socket.io bağlantısını kapat
    return () => {
      newSocket.disconnect();
    };
  }, []);

  // Sohbet geçmişini yükle
  useEffect(() => {
    const fetchChatHistory = async () => {
      try {
        const token = localStorage.getItem('token');
        
        if (!token) return;
        
        const response = await axios.get('http://localhost:5000/api/chat-history', {
          headers: { Authorization: `Bearer ${token}` }
        });
        
        if (response.data.chatHistory) {
          setMessages(response.data.chatHistory);
        } else {
          // Kullanıcı ilk kez sohbet ediyorsa karşılama mesajı
          setMessages([
            { 
              role: 'assistant', 
              content: `Merhaba! 😊 Diyet Dostu'na hoş geldin!

Ben senin kişisel beslenme ve spor asistanınım. Türk mutfağına uygun, kolay ve sağlıklı tarifler sunarak diyet hedeflerine ulaşmana yardımcı olacağım.

Sana daha iyi hizmet verebilmem için biraz kendinden bahseder misin?
• Yaşın kaç?
• Boyun ve kilon nedir?
• Hedefin nedir? (Kilo vermek, kilo almak, kas kazanmak, sağlıklı beslenmek vb.)
• Herhangi bir besin alerjin veya özel diyet tercühin var mı? (vejetaryen, glütensiz, vb.)

İşte sana yardımcı olabileceğim konular:
🥗 Türk mutfağına uygun düşük kalorili/yüksek proteinli tarifler
🏋️‍♀️ Kişiselleştirilmiş egzersiz planları
📸 Yemek fotoğraflarını analiz ederek besin değeri tahmini
📝 Haftalık beslenme programı oluşturma
📊 Kilo takibi ve gelişim analizi

Hadi birlikte sağlıklı bir yaşam yolculuğuna başlayalım! 💪` 
            }
          ]);
        }
      } catch (error) {
        console.error('Sohbet geçmişi yüklenirken hata:', error);
      }
    };

    fetchChatHistory();
  }, []);

  // Otomatik kaydırma
  useEffect(() => {
    if (messagesEndRef.current) {
      messagesEndRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  }, [messages]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!input.trim()) return;
    
    // Kullanıcı mesajını ekran üzerinde göster
    const userMessage = { role: 'user', content: input };
    setMessages((prev) => [...prev, userMessage]);
    
    setInput('');
    setIsLoading(true);
    
    try {
      const token = localStorage.getItem('token');
      
      if (!token) {
        throw new Error('Oturum zaman aşımına uğradı. Lütfen tekrar giriş yapın.');
      }
      
      // Asistan cevabını al
      await axios.post('http://localhost:5000/api/chat', 
        { message: input },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      
      // Socket.io ile yanıt gelecek, burada bekleme yapmıyoruz
    } catch (error) {
      console.error('Mesaj gönderilirken hata:', error);
      setMessages((prev) => [
        ...prev, 
        { 
          role: 'assistant', 
          content: 'Üzgünüm, bir hata oluştu. Lütfen daha sonra tekrar deneyin.'
        }
      ]);
    } finally {
      setIsLoading(false);
    }
  };

  const handleImageUpload = async (imageFile) => {
    setShowImageUploader(false);
    
    // Yükleniyor mesajı
    setMessages((prev) => [
      ...prev, 
      { role: 'user', content: 'Fotoğraf yüklendi:', image: URL.createObjectURL(imageFile) }
    ]);
    
    setIsLoading(true);
    
    try {
      const token = localStorage.getItem('token');
      
      if (!token) {
        throw new Error('Oturum zaman aşımına uğradı. Lütfen tekrar giriş yapın.');
      }
      
      // FormData oluştur
      const formData = new FormData();
      formData.append('image', imageFile);
      
      // Görüntü analiz endpoint'ine gönder
      await axios.post(
        'http://localhost:5000/api/analyze-image', 
        formData,
        { 
          headers: { 
            Authorization: `Bearer ${token}`,
            'Content-Type': 'multipart/form-data'
          } 
        }
      );
      
      // Socket.io ile yanıt gelecek
    } catch (error) {
      console.error('Görüntü analizi sırasında hata:', error);
      setMessages((prev) => [
        ...prev, 
        { 
          role: 'assistant', 
          content: 'Üzgünüm, görüntü analiz edilirken bir hata oluştu. Lütfen daha sonra tekrar deneyin.'
        }
      ]);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="chat-container">
      <div className="bg-white rounded-lg shadow-md overflow-hidden flex flex-col h-[70vh]">
        <div className="p-4 bg-diet-green text-white">
          <h2 className="text-xl font-semibold">Diyet Dostu 🥗</h2>
          <p className="text-sm">Sağlıklı yaşam asistanınız</p>
        </div>
        
        <div className="flex-grow overflow-y-auto p-4">
          {messages.map((message, index) => (
            <ChatBubble 
              key={index} 
              message={message.content}
              image={message.image}
              isUser={message.role === 'user'} 
            />
          ))}
          
          {isLoading && (
            <div className="flex items-center space-x-2 text-gray-500 p-3 bg-gray-100 rounded-lg max-w-[80%] mr-auto">
              <div className="flex space-x-1">
                <div className="w-2 h-2 rounded-full bg-diet-green animate-bounce" style={{ animationDelay: '0ms' }}></div>
                <div className="w-2 h-2 rounded-full bg-diet-green animate-bounce" style={{ animationDelay: '300ms' }}></div>
                <div className="w-2 h-2 rounded-full bg-diet-green animate-bounce" style={{ animationDelay: '600ms' }}></div>
              </div>
              <p>Diyet Dostu yanıt yazıyor...</p>
            </div>
          )}
          
          <div ref={messagesEndRef} />
        </div>
        
        {showImageUploader ? (
          <ImageUploader 
            onUpload={handleImageUpload} 
            onCancel={() => setShowImageUploader(false)}
          />
        ) : (
          <form onSubmit={handleSubmit} className="p-4 border-t">
            <div className="flex space-x-2">
              <button
                type="button"
                onClick={() => setShowImageUploader(true)}
                className="p-2 bg-diet-blue text-white rounded-full hover:bg-blue-500 transition-colors"
              >
                <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={1.5} stroke="currentColor" className="w-6 h-6">
                  <path strokeLinecap="round" strokeLinejoin="round" d="M2.25 15.75l5.159-5.159a2.25 2.25 0 013.182 0l5.159 5.159m-1.5-1.5l1.409-1.409a2.25 2.25 0 013.182 0l2.909 2.909m-18 3.75h16.5a1.5 1.5 0 001.5-1.5V6a1.5 1.5 0 00-1.5-1.5H3.75A1.5 1.5 0 002.25 6v12a1.5 1.5 0 001.5 1.5zm10.5-11.25h.008v.008h-.008V8.25zm.375 0a.375.375 0 11-.75 0 .375.375 0 01.75 0z" />
                </svg>
              </button>
              
              <input
                type="text"
                className="message-input flex-grow"
                placeholder="Bir mesaj yazın..."
                value={input}
                onChange={(e) => setInput(e.target.value)}
                disabled={isLoading}
              />
              
              <button
                type="submit"
                className="send-button"
                disabled={isLoading || !input.trim()}
              >
                <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={1.5} stroke="currentColor" className="w-6 h-6">
                  <path strokeLinecap="round" strokeLinejoin="round" d="M6 12L3.269 3.126A59.768 59.768 0 0121.485 12 59.77 59.77 0 013.27 20.876L5.999 12zm0 0h7.5" />
                </svg>
              </button>
            </div>
          </form>
        )}
      </div>
    </div>
  );
};

export default Chat;
